INSERT INTO `post` (`id`, `title`, `description`, `content`, `image`, `read_count`, `category_id`, `user_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(1, 'Aut quod quo dolore a incididunt sunt possimus ea nostrud aut omnis nihil sint', 'Nam et eos est maiores quia doloribus sed eaque dignissimos voluptatem aut cupidatat ea in.', '<p>Nam et eos est maiores quia doloribus sed eaque dignissimos voluptatem aut cupidatat ea in.</p>', '', 1, 4, 6, '2017-04-18 18:30:16', '2017-04-22 10:03:34', '2017-04-22 10:03:34'),
(2, 'Doloribus incididunt rem obcaecati dolor voluptate minus', 'Placeat, quas mollitia labore inventore vel nisi illum, eum quos voluptatem, error ut.', '<p>Placeat, quas mollitia labore inventore vel nisi illum, eum quos voluptatem, error ut.</p>', '1492565429.jpg', 1, 4, 6, '2017-04-18 18:30:29', '2017-04-22 10:03:34', '2017-04-22 10:03:34'),
(3, 'Tempore quod quibusdam eiusmod non id consectetur nostrum ut voluptatem est mollitia dolore consectetur qui fuga Consectetur inventore', 'Eligendi voluptate ullam eum autem non adipisicing cumque sunt laudantium, ut voluptas sed deleniti do itaque non illo.', '<p>Eligendi voluptate ullam eum autem non adipisicing cumque sunt laudantium, ut voluptas sed deleniti do itaque non illo.</p>', '', 1, 4, 6, '2017-04-19 03:01:07', '2017-04-22 10:03:34', '2017-04-22 10:03:34'),
(4, 'Sed consectetur sint laborum Rem sit autem velit', 'Aliqua. Saepe eveniet, iste unde itaque dolore nobis ad laboris provident, consequat. Sed eos.', '<p>Aliqua. Saepe eveniet, iste unde itaque dolore nobis ad laboris provident, consequat. Sed eos.</p>', '', 1, 4, 6, '2017-04-20 20:51:35', '2017-04-22 10:03:34', '2017-04-22 10:03:34'),
(5, 'Nostrud dicta ratione in nesciunt eiusmod', 'Rerum voluptas nisi maiores voluptas error asperiores voluptatem fuga. Ducimus, duis mollit voluptatibus dolorem eu vitae consectetur dolore excepturi.', '<p>Rerum voluptas nisi maiores voluptas error asperiores voluptatem fuga. Ducimus, duis mollit voluptatibus dolorem eu vitae consectetur dolore excepturi.</p>', '', 1, 4, 6, '2017-04-22 10:04:52', '2017-04-22 10:05:10', '2017-04-22 10:05:10'),
(6, 'Mơ về những chòm sao', 'Là nơi chia sẻ đam mê về vũ trụ, không gian, thiên văn học, vật lý thiên văn… thời gian gần đây, các câu lạc bộ thiên văn nghiệp dư được thành lập ngày càng nhiều, ở khắp các tỉnh thành, đã và đang thỏa mãn niềm đam mê khoa học của các bạn trẻ.', '<p>L&agrave; nơi chia sẻ đam m&ecirc; về vũ trụ, kh&ocirc;ng gian, thi&ecirc;n văn học, vật l&yacute; thi&ecirc;n văn&hellip; thời gian gần đ&acirc;y, c&aacute;c c&acirc;u lạc bộ thi&ecirc;n văn nghiệp dư được th&agrave;nh lập ng&agrave;y c&agrave;ng nhiều, ở khắp c&aacute;c tỉnh th&agrave;nh, đ&atilde; v&agrave; đang thỏa m&atilde;n niềm đam m&ecirc; khoa học của c&aacute;c bạn trẻ.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/thienvan_zps6vbce28c.jpg" /></p>\r\n\r\n<p><em>C&aacute;c th&agrave;nh vi&ecirc;n CLB thi&ecirc;n văn Đ&agrave; Nẵng b&ecirc;n 2 bộ k&iacute;nh thi&ecirc;n văn tự tạo - Ảnh: CLB thi&ecirc;n văn Đ&agrave; Nẵng cung cấp</em></p>\r\n\r\n<p>Tr&ecirc;n b&atilde;i biển Đ&agrave; Nẵng một ng&agrave;y đầy nắng, bầu trời trong xanh, gần cả trăm thanh ni&ecirc;n đa phần tuổi từ 16 đến 30 h&agrave;o hứng, say sưa với tr&ograve; chơi bắn t&ecirc;n lửa nước. Đ&acirc;y l&agrave; một tr&ograve; chơi kh&aacute; lạ đối với những bạn trẻ b&igrave;nh thường nhưng l&agrave; tr&ograve; chơi th&uacute; vị v&agrave; quen thuộc của th&agrave;nh vi&ecirc;n c&aacute;c c&acirc;u lạc bộ (CLB) thi&ecirc;n văn nghiệp dư.</p>\r\n\r\n<p>Mỗi m&ocirc; h&igrave;nh t&ecirc;n lửa được thiết kế rất đặc biệt, đẹp mắt v&agrave; s&aacute;ng tạo. &ldquo;Nguy&ecirc;n l&yacute; hoạt động của t&ecirc;n lửa nước rất đơn giản. Lấy một vỏ chai nước bằng nhựa tạo h&igrave;nh t&ecirc;n lửa v&agrave; đổ khoảng 2/3 nước v&agrave;o, sau đ&oacute; gh&eacute;p với một gi&agrave;n ph&oacute;ng bằng ống nước c&oacute; bộ phận cung cấp kh&iacute; n&eacute;n v&agrave;o t&ecirc;n lửa. D&ugrave;ng bơm tạo &aacute;p suất, đến khi đủ &aacute;p suất cần thiết th&igrave; mở van cho t&ecirc;n lửa bắn đi. Đơn giản vậy nhưng t&ecirc;n lửa nước l&agrave; tr&ograve; chơi đặc biệt hấp dẫn c&aacute;c bạn m&ecirc; khoa học vật l&yacute;, thi&ecirc;n văn&rdquo; - Nguyễn Đức Hạnh, th&agrave;nh vi&ecirc;n CLB thi&ecirc;n văn nghiệp dư Quảng Ng&atilde;i cho biết.</p>\r\n\r\n<p>Trong hơn 2 năm qua, h&agrave;ng trăm bạn trẻ y&ecirc;u thi&ecirc;n văn học tại H&agrave; Nội, TP.HCM, Huế, Đ&agrave; Nẵng, Quảng Nam, Quảng Ng&atilde;i, B&igrave;nh Định đ&atilde; kết nối với nhau, c&ugrave;ng nghi&ecirc;n cứu, thảo luận, chia sẻ kiến thức, niềm đam m&ecirc; thi&ecirc;n văn học th&ocirc;ng qua c&aacute;c diễn đ&agrave;n, mạng x&atilde; hội v&agrave; ng&agrave;y hội thi&ecirc;n văn nghiệp dư được tổ chức hằng năm.</p>\r\n\r\n<p>Đến với c&aacute;c CLB thi&ecirc;n văn nghiệp dư, bạn trẻ kh&ocirc;ng chỉ bị hấp dẫn bởi những tr&ograve; chơi hướng đến kh&aacute;m ph&aacute; vũ trụ, kh&ocirc;ng gian như bắn t&ecirc;n lửa nước, thả trứng m&ocirc; phỏng t&agrave;u đổ bộ, thiết kế, chế tạo k&iacute;nh thi&ecirc;n văn&hellip; m&agrave; c&ograve;n được chia sẻ kinh nghiệm quan s&aacute;t bầu trời, vị tr&iacute; c&aacute;c ch&ograve;m sao.</p>\r\n\r\n<p>&ldquo;Quan s&aacute;t bầu trời đ&ecirc;m th&aacute;ng 8 c&oacute; thể x&aacute;c định vị tr&iacute; c&aacute;c ch&ograve;m sao như Thần n&ocirc;ng, Đại h&ugrave;ng tinh, Nh&acirc;n m&atilde;, Tam gi&aacute;c m&ugrave;a h&egrave;; nối 3 ng&ocirc;i sao s&aacute;ng nhất của c&aacute;c ch&ograve;m sao Thi&ecirc;n ưng, Thi&ecirc;n nga v&agrave; Thi&ecirc;n cầm&hellip; C&oacute; nhiều kiến thức kh&oacute; t&igrave;m thấy v&agrave; tiếp nhận từ s&aacute;ch vở m&agrave; phải qua trải nghiệm mới c&oacute; được. Kết nối với nhau, th&agrave;nh vi&ecirc;n c&aacute;c CLB thi&ecirc;n văn nghiệp dư sẽ c&oacute; cơ hội chia sẻ c&ugrave;ng nhau những kiến thức bổ &iacute;ch, lan tỏa những gi&aacute; trị đ&iacute;ch thực của niềm đam m&ecirc; nghi&ecirc;n cứu vật l&yacute;, thi&ecirc;n văn&hellip;&rdquo; - Đặng Tuấn Duy, th&agrave;nh vi&ecirc;n CLB thi&ecirc;n văn TP.HCM giải th&iacute;ch.</p>\r\n\r\n<p>Lu&ocirc;n đồng h&agrave;nh v&agrave; kh&aacute; s&acirc;u s&aacute;t với hoạt động của c&aacute;c CLB thi&ecirc;n văn nghiệp dư trong cả nước, tiến sĩ Nguyễn Thị Ho&agrave;ng Anh (Trung t&acirc;m vệ tinh quốc gia) cho biết: &ldquo;Đến với c&aacute;c CLB thi&ecirc;n văn như thế n&agrave;y, bạn trẻ dễ d&agrave;ng gặp gỡ những người c&oacute; chung sự quan t&acirc;m, thỏa m&atilde;n đam m&ecirc; c&ocirc;ng nghệ, kh&aacute;m ph&aacute; kh&ocirc;ng gian, vũ trụ. Những hoạt động bề nổi như chế tạo k&iacute;nh viễn vọng, thiết kế t&ecirc;n lửa nước rất hấp dẫn v&agrave; khơi gợi t&ograve; m&ograve;, kh&aacute;m ph&aacute; khoa học, từ đ&oacute;, c&aacute;c bạn nu&ocirc;i dưỡng đam m&ecirc; v&agrave; đi dần đến con đường chuy&ecirc;n nghiệp&rdquo;.</p>\r\n\r\n<p>Giữa c&aacute;i n&oacute;ng của trưa h&egrave; miền Trung, lẫn trong cộng đồng những người trẻ y&ecirc;u thi&ecirc;n văn l&agrave; PGS-TS Guillaume Patanchon (ĐH Paris Diderot, Ph&aacute;p), hiện l&agrave; giảng vi&ecirc;n chương tr&igrave;nh đ&agrave;o tạo thi&ecirc;n văn, h&agrave;ng kh&ocirc;ng (Trường ĐH Khoa học - C&ocirc;ng nghệ H&agrave; Nội) đ&atilde; gi&uacute;p c&aacute;c bạn rất nhiều khi thực tế h&oacute;a những l&yacute; thuyết, những kh&aacute;i niệm của một lĩnh vực c&ograve;n kh&aacute; mới ở VN. &ldquo;D&ugrave; học vị cao, nhưng anh ấy c&ograve;n rất trẻ, ngo&agrave;i 35 tuổi, n&ecirc;n anh ấy c&oacute; c&aacute;ch rất ri&ecirc;ng để truyền đạt những nguy&ecirc;n tắc, nguy&ecirc;n l&yacute; cơ bản về vật l&yacute;, lan tỏa giấc mơ thi&ecirc;n văn đến những người trẻ&rdquo; - Th&aacute;i Văn Lợi, th&agrave;nh vi&ecirc;n CLB thi&ecirc;n văn nghiệp dư Đ&agrave; Nẵng n&oacute;i về người bạn đồng h&agrave;nh đặc biệt của m&igrave;nh.</p>\r\n\r\n<p>&ldquo;Kết nối với c&aacute;c CLB thi&ecirc;n văn nghiệp dư ở VN, t&ocirc;i gặp nhiều bạn tuổi mới lớn nhưng đ&atilde; thể hiện một niềm đam m&ecirc; đặc biệt, một nền tảng kiến thức nhất định đối với vật l&yacute;, thi&ecirc;n văn học&hellip; Nếu c&oacute; cơ hội, được đ&agrave;o tạo chuy&ecirc;n nghiệp, họ sẽ l&agrave; nguồn nh&acirc;n lực triển vọng&rdquo; - TS Guillaume Patanchon khẳng định.</p>\r\n\r\n<p style="text-align:right"><em><strong>An Dy&nbsp;-&nbsp;B&aacute;o Thanh Ni&ecirc;n</strong></em></p>', '1492910383.jpg', 2, 4, 6, '2017-04-22 18:19:43', '2017-04-26 02:08:32', NULL),
(7, 'Giới trẻ Đà Nẵng hụt hẫng vì không nhìn rõ “trăng máu”', 'Khoảng 17h ngày 8/10, các bạn trẻ trong CLB Thiên Văn Đà Nẵng và nhiều bạn trẻ khác đã tập trung bên bờ biển Nguyễn Tất Thành (đoạn giao với đường Nguyễn Sinh Sắc) để được tận mắt chiêm ngưỡng hiện tượng kỳ thú nguyệt thực toàn phần (hay còn gọi là trăng máu).', '<p>(D&acirc;n tr&iacute;) - Tại Đ&agrave; Nẵng, đến khoảng 18h13 ng&agrave;y 8/10 mặt trăng mới bắt đầu xuất hiện nhưng chỉ rất mờ v&agrave; chỉ được khoảng 1 ph&uacute;t sau th&igrave; bị m&acirc;y che lấp&hellip;</p>\r\n\r\n<p>Khoảng 17h ng&agrave;y 8/10, c&aacute;c bạn trẻ trong CLB Thi&ecirc;n Văn Đ&agrave; Nẵng v&agrave; nhiều bạn trẻ kh&aacute;c đ&atilde; tập trung b&ecirc;n bờ biển Nguyễn Tất Th&agrave;nh (đoạn giao với đường Nguyễn Sinh Sắc) để được tận mắt chi&ecirc;m ngưỡng hiện tượng kỳ th&uacute; nguyệt thực to&agrave;n phần (hay c&ograve;n gọi l&agrave; trăng m&aacute;u).</p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/IMG_0724-fda2a_zpswhcfhm85.jpg" /></p>\r\n\r\n<p><em>C&aacute;c bạn trẻ Đ&agrave; Nẵng chăm ch&uacute; theo d&otilde;i mỗi khi trăng xuất hiện nhưng c&oacute; phần hụt hẫng v&igrave; kh&ocirc;ng nh&igrave;n r&otilde; được hiện tượng &quot;trăng m&aacute;u&quot;.</em></p>\r\n\r\n<p>Theo bạn Th&aacute;i Văn Lợi &ndash; chủ nhiệm CLB Thi&ecirc;n Văn Đ&agrave; Nẵng, để chuẩn bị cho đợt xem nguyệt thực lần n&agrave;y CLB đ&atilde; l&ecirc;n kế hoạch, chọn địa điểm trong v&ograve;ng một tuần. H&ocirc;m nay, CLB đ&atilde; chuẩn bị 2 k&iacute;nh thi&ecirc;n văn (1 k&iacute;nh mua, 1 k&iacute;nh tự chế) để quan s&aacute;t nguyệt thực.</p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/anh-1-fda2a_zpslfppksxs.jpg" /></p>\r\n\r\n<p><em>Tại Đ&agrave; Nẵng, đến khoảng 18h13, mặt trăng mới xuất hiện nhưng rất mờ v&agrave; biến mất chỉ sau khoảng 1 ph&uacute;t</em></p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/anh-2-fda2a_zpss5zhj0lv.jpg" /></p>\r\n\r\n<p><em>Đến khoảng 18h38, mặt trăng mới xuất hiện trở lại v&agrave; cũng biến mất sau khoảng v&agrave;i ph&uacute;t</em></p>\r\n\r\n<p>Tuy nhi&ecirc;n, đợi m&atilde;i đến khoảng 18h13, mặt trăng mới bắt đầu l&oacute; ra. Tuy nhi&ecirc;n rất mờ v&agrave; chỉ được khoảng 1 ph&uacute;t th&igrave; bị m&acirc;y che lấp. Đến khoảng 18h38, mặt trăng xuất hiện trở lại nhưng chỉ bằng h&igrave;nh lưỡi liềm, trăng m&agrave;u v&agrave;ng chứ kh&ocirc;ng c&oacute; nhuộm m&agrave;u đỏ. V&agrave; được khoảng 2 ph&uacute;t sau th&igrave; trăng lại biến mất.<br />\r\nNhững lần sau trăng vừa xuất hiện được 1 &ndash; 2 ph&uacute;t th&igrave; lại biến mất. Cho đến khoảng 19h30, khi hiện tượng nguyệt thực kết th&uacute;c th&igrave; trăng mới hiện ra r&otilde; r&agrave;ng.</p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/anh-3-fda2a_zpsmmequ9xj.jpg" /></p>\r\n\r\n<p><em>Những lần xuất hiện sau đ&oacute;, mặt trăng cũng kh&ocirc;ng r&otilde; bị nhuộm m&agrave;u đỏ</em></p>\r\n\r\n<p>&ldquo;Mấy ng&agrave;y vừa rồi thời tiết Đ&agrave; Nẵng thường xuy&ecirc;n mưa n&ecirc;n bọn em lo kh&ocirc;ng thể tổ chức ngắm nguyệt thực. Tuy nhi&ecirc;n, ng&agrave;y h&ocirc;m nay, thời tiết Đ&agrave; Nẵng nắng r&aacute;o n&ecirc;n ai cũng rất h&agrave;o hức. Thế nhưng, do bị m&acirc;y che lấp, thỉnh thoảng trăng mới xuất hiện, kh&ocirc;ng nh&igrave;n được r&otilde; n&ecirc;n xem kh&ocirc;ng sướng&rdquo;, bạn Th&aacute;i Văn Lợi chia sẻ.</p>\r\n\r\n<p>C&ograve;n bạn Đo&agrave;n Phương Dung, th&agrave;nh vi&ecirc;n của CLB Thi&ecirc;n Văn Đ&agrave; Nẵng cho biết, trước khi đi em cứ nghĩ xem được tận mắt chi&ecirc;m ngưỡng mặt trăng nhuốm m&agrave;u đỏ nhưng lại kh&ocirc;ng được thế n&ecirc;n hơi hụt hẫng.</p>\r\n\r\n<p style="text-align:right"><em><strong>Kh&aacute;nh Hồng</strong></em></p>\r\n\r\n<p style="text-align:right"><em><a href="http://dantri.com.vn/nhip-song-tre/gioi-tre-da-nang-hut-hang-vi-khong-nhin-ro-trang-mau-953484.htm" rel="nofollow" target="_blank">http://dantri.com.vn/nhip-song-tre/gioi-tre-da-nang-hut-hang-vi-khong-nhin-ro-trang-mau-953484.htm</a></em></p>', '1492910486.jpg', 3, 4, 6, '2017-04-22 18:21:26', '2017-04-27 02:49:34', NULL),
(8, 'Những địa điểm xem “Mặt trăng máu” lý tưởng nhất vào ngày mai', 'Vào ngày mai 4/4 hiện tượng Mặt trăng máu hay còn gọi là nguyệt thực toàn phần sẽ diễn ra trong khoảng 4 phút 43 giây. Để có thể quan sát hiện tượng này, các chuyên gia khuyến cáo người dân nên chọn các địa điểm có hướng đông tốt và xa ánh đèn để không bị che tầm nhìn.', '<p><strong>V&agrave;o ng&agrave;y mai 4/4 hiện tượng Mặt trăng m&aacute;u hay c&ograve;n gọi l&agrave;&nbsp;nguyệt thực to&agrave;n phần&nbsp;sẽ diễn ra trong khoảng 4 ph&uacute;t 43 gi&acirc;y. Để c&oacute; thể quan s&aacute;t hiện tượng n&agrave;y, c&aacute;c chuy&ecirc;n gia khuyến c&aacute;o người d&acirc;n n&ecirc;n chọn c&aacute;c địa điểm c&oacute; hướng đ&ocirc;ng tốt v&agrave; xa &aacute;nh đ&egrave;n để kh&ocirc;ng bị che tầm nh&igrave;n.</strong><br />\r\n<strong>C&aacute;c địa điểm c&oacute; thể quan s&aacute;t tốt hiện tượng Mặt trăng m&aacute;u.</strong></p>\r\n\r\n<p><strong>Tại H&agrave; Nội</strong></p>\r\n\r\n<p>Địa điểm được xem l&agrave; tốt nhất để theo d&otilde;i hiện tượng Mặt trăng m&aacute;u hay c&ograve;n gọi l&agrave; nguyệt thực to&agrave;n phần l&agrave; &nbsp;Cột đồng hồ S&acirc;n vận động quốc gia Mỹ Đ&igrave;nh.</p>\r\n\r\n<p>Theo anh Phương &ndash; Chủ nhiệm Hội Thi&ecirc;n văn Nghiệp dư H&agrave; Nội cho biết nếu kh&ocirc;ng &nbsp;c&oacute; điều kiện đến s&acirc;n Mỹ Đ&igrave;nh th&igrave; c&oacute; thể t&igrave;m nơi n&agrave;o c&oacute; hướng đ&ocirc;ng tốt, rộng r&atilde;i, tho&aacute;ng đ&atilde;ng để quan s&aacute;t.</p>\r\n\r\n<p>Hội Thi&ecirc;n văn Nghiệp dư H&agrave; Nội b&ecirc;n cạnh việc hỗ trợ thiết bị th&igrave; c&ograve;n giải đ&aacute;p nhiều c&acirc;u hỏi của c&aacute;c bạn tham gia quan s&aacute;t sự kiện.</p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i1067.photobucket.com/albums/u437/gemini4869/trang_mau_1_zpspvlpxeas.jpg" /></p>\r\n\r\n<p><em>Mặt trăng m&aacute;u năm ngo&aacute;i tại Mỹ - Ảnh: The Virtual Telescope Project</em></p>\r\n\r\n<p><strong>Tại Đ&agrave; Nẵng</strong></p>\r\n\r\n<p>Những người quan t&acirc;m đến nguyệt thực c&oacute; thể đến C&ocirc;ng vi&ecirc;n biển Đ&ocirc;ng (biển Phạm Văn Đồng) từ 16h30 đến 21h30.</p>\r\n\r\n<p>Theo anh Th&aacute;i Văn Lợi -Chủ nhiệm C&acirc;u lạc bộ (CLB) Thi&ecirc;n văn học Đ&agrave; Nẵng, người xem n&ecirc;n chọn những địa điểm c&oacute; hướng đ&ocirc;ng nh&igrave;n ra biển, dự kiến 19h l&agrave; thời điểm nguyệt thực cực đại.</p>\r\n\r\n<p><strong>Tại TP Hồ Ch&iacute; Minh</strong></p>\r\n\r\n<p>Người quan t&acirc;m c&oacute; thể đến Khu D&acirc;n cư T&acirc;n Quy Đ&ocirc;ng, Quận 7 để quan s&aacute;t nguyệt thực v&agrave;o tối mai, anh Đặng Tuấn Duy, Hội thi&ecirc;n văn nghiệp dư TP. Hồ Ch&iacute; Minh cho biết.</p>\r\n\r\n<p>Ngo&agrave;i ra, bến Nh&agrave; Rồng, Metro Quận 2 cũng l&agrave; c&aacute;c địa điểm tốt để quan s&aacute;t nguyệt thực. Nơi quan s&aacute;t n&ecirc;n tr&aacute;nh xa &aacute;nh đ&egrave;n v&agrave; hơi tối. Hội thi&ecirc;n văn dự kiến sẽ giải đ&aacute;p nhiều c&acirc;u hỏi của những người y&ecirc;u th&iacute;ch khoa học như nguyệt thực c&oacute; diễn ra thường xuy&ecirc;n kh&ocirc;ng, tại sao c&oacute; m&agrave;u đỏ, hiện tượng trăng m&aacute;u l&agrave; g&igrave;.</p>\r\n\r\n<p>Từ 16h01 giờ H&agrave; Nội ng&agrave;y mai, Mặt Trăng sẽ đi v&agrave;o v&ugrave;ng b&oacute;ng nửa tối. Pha một phần bắt đầu l&uacute;c 17h15, pha to&agrave;n phần bắt đầu từ 18h57 v&agrave; đạt cực đại l&uacute;c 19h00. Trong năm 2014, người y&ecirc;u thi&ecirc;n văn ở Việt Nam từng quan s&aacute;t hiện tượng tương tự ng&agrave;y 8/10. Nguyệt thực một phần tiếp theo ở Việt Nam sẽ diễn ra ng&agrave;y 8/8/2017. Nguyệt thực to&agrave;n phần xuất hiện một năm sau đ&oacute;, v&agrave;o ng&agrave;y 31/1/2018.</p>\r\n\r\n<p style="text-align:right"><em><strong>Nha Trang</strong></em></p>\r\n\r\n<p style="text-align:right"><em><a href="http://toancanh.nguoiduatin.vn/nhung-dia-diem-xem-mat-trang-mau-ly-tuong-nhat-vao-ngay-mai-011352282.html" rel="nofollow" target="_blank">http://toancanh.nguoiduatin.vn/nhung-dia-diem-xem-mat-trang-mau-ly-tuong-nhat-vao-ngay-mai-011352282.html</a></em></p>', '1492910558.jpg', 2, 4, 6, '2017-04-22 18:22:38', '2017-04-26 02:11:43', NULL),
(9, 'Giới trẻ Đà Nẵng xếp hàng ngắm nguyệt thực', 'Chiều tối 4/4, các bạn trẻ trong CLB Thiên văn học Đà Nẵng cùng nhiều bạn trẻ và người dân yêu thích thiên văn trên địa bàn TP Đà Nẵng đã tập trung tại Công viên biển Đông để được tận mắt quan sát hiện tượng nguyệt thực toàn phần.', '<p>Chiều tối 4/4, c&aacute;c bạn trẻ trong CLB Thi&ecirc;n văn học Đ&agrave; Nẵng c&ugrave;ng nhiều bạn trẻ v&agrave; người d&acirc;n y&ecirc;u th&iacute;ch thi&ecirc;n văn tr&ecirc;n địa b&agrave;n TP Đ&agrave; Nẵng đ&atilde; tập trung tại C&ocirc;ng vi&ecirc;n biển Đ&ocirc;ng để được tận mắt quan s&aacute;t hiện tượng nguyệt thực to&agrave;n phần.</p>\r\n\r\n<p>Bạn Th&aacute;i Văn Lợi &ndash; Chủ nhiệm CLB Thi&ecirc;n văn học Đ&agrave; Nẵng cho biết: Tham gia quan s&aacute;t hiện tượng nguyệt thực do CLB tổ chức c&oacute; 40 th&agrave;nh vi&ecirc;n của CLB v&agrave; hơn 150 bạn trẻ l&agrave; sinh vi&ecirc;n c&aacute;c trường ĐH, CĐ v&agrave; c&aacute;c em học sinh tr&ecirc;n địa b&agrave;n đăng k&yacute;.</p>\r\n\r\n<p>Mọi người ra đ&acirc;y l&uacute;c 16h chiều để c&oacute; thể quan s&aacute;t được tượng nguyệt thực từ l&uacute;c mới bắt đầu xuất hiện. CLB c&oacute; 1 ống nh&ograve;m v&agrave; 4 k&iacute;nh thi&ecirc;n văn để phục vụ mọi người quan s&aacute;t hiện tượng nguyệt thực được dễ d&agrave;ng hơn.</p>\r\n\r\n<p>&ldquo;Tại Đ&agrave; Nẵng, hiện tượng nguyệt thực c&oacute; thể thấy được khi mặt trăng chưa mọc. Tuy nhi&ecirc;n, nguyệt thực bị m&acirc;y che lấp n&ecirc;n nh&igrave;n kh&ocirc;ng được r&otilde; lắm&rdquo;, bạn Th&aacute;i Văn Lợi cho biết th&ecirc;m.&nbsp;</p>\r\n\r\n<p>V&igrave; số lượng ống nh&ograve;m v&agrave; k&iacute;nh thi&ecirc;n văn &iacute;t nhưng số lượng người xem đ&ocirc;ng n&ecirc;n c&aacute;c bạn phải xếp h&agrave;ng chờ đến lượt m&igrave;nh. D&ugrave; nguyệt thực nh&igrave;n kh&ocirc;ng được r&otilde; lắm nhưng ai cũng tỏ ra rất h&aacute;o hức.</p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i1067.photobucket.com/albums/u437/gemini4869/anh-1-50708_zpsawiwutjv.jpg" /></p>\r\n\r\n<p style="text-align:center"><em>Địa điểm ngắm nguyệt thực của c&aacute;c bạn trẻ n&agrave;y l&agrave; C&ocirc;ng vi&ecirc;n biển Đ&ocirc;ng</em>&nbsp;</p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i1067.photobucket.com/albums/u437/gemini4869/anh-2-50708_zpsx5komk6j.jpg" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i1067.photobucket.com/albums/u437/gemini4869/anh-3-50708_zpsqtewxqaw.jpg" /></p>\r\n\r\n<p style="text-align:center"><em>C&aacute;c bạn trẻ rất h&aacute;o hức khi được quan s&aacute;t nguyệt thực to&agrave;n phần mặc d&ugrave; kh&ocirc;ng được r&otilde; lắm</em></p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i1067.photobucket.com/albums/u437/gemini4869/anh-4-50708_zpso9bsfaw5.jpg" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i1067.photobucket.com/albums/u437/gemini4869/anh-5-50708_zpsdj93c0hx.jpg" /></p>\r\n\r\n<p style="text-align:center"><em>Nguyệt thực bị m&acirc;y che lấp n&ecirc;n nh&igrave;n kh&ocirc;ng r&otilde;</em></p>\r\n\r\n<p style="text-align:right"><em><strong>Kh&aacute;nh Hồng</strong></em></p>\r\n\r\n<p style="text-align:right"><em><a href="http://dantri.com.vn/nhip-song-tre/gioi-tre-da-nang-xep-hang-ngam-nguyet-thuc-duy-nhat-trong-nam-1055091.htm">http://dantri.com.vn/nhip-song-tre/gioi-tre-da-nang-xep-hang-ngam-nguyet-thuc-duy-nhat-trong-nam-1055091.htm</a></em></p>', '1492911536.jpg', 1, 4, 6, '2017-04-22 18:38:56', '2017-04-22 18:38:56', NULL),
(10, 'Giới trẻ Đà Nẵng háo hức khi được xem “trăng máu”', 'Tối 4/4, CLB Thiên văn học Đà Nẵng đã tổ chức hoạt động quan sát nguyệt thực từ lúc 16h30-21h30 tại công viên biển Đông (bãi biển Phạm Văn Đồng). Hàng nghìn người dân, bạn trẻ hiếu kỳ đã có mặt cuối giờ chiều để chiêm ngưỡng hiện tượng độc đáo hiếm thấy này.', '<p>TPO - Tối 4/4, CLB Thi&ecirc;n văn học Đ&agrave; Nẵng đ&atilde; tổ chức hoạt động quan s&aacute;t nguyệt thực từ l&uacute;c 16h30-21h30 tại c&ocirc;ng vi&ecirc;n biển Đ&ocirc;ng (b&atilde;i biển Phạm Văn Đồng). H&agrave;ng ngh&igrave;n người d&acirc;n, bạn trẻ hiếu kỳ đ&atilde; c&oacute; mặt cuối giờ chiều để chi&ecirc;m ngưỡng hiện tượng độc đ&aacute;o hiếm thấy n&agrave;y.</p>\r\n\r\n<p>Nguyễn Ngọc Trường Giang, SV ĐH Ngoại ngữ n&oacute;i: &ldquo;Bọn m&igrave;nh nghe tin tối nay sẽ xuất hiện nguyệt thực to&agrave;n phần n&ecirc;n cả nh&oacute;m tập trung từ sớm tại bờ biển. Đ&acirc;y l&agrave; địa điểm l&yacute; tưởng, hi vọng tối nay tất cả mọi người sẽ được m&atilde;n nh&atilde;n&rdquo;.</p>\r\n\r\n<p>Anh Th&aacute;i Văn Lợi, chủ nhiệm CLB Thi&ecirc;n văn học Đ&agrave; Nẵng cho biết, c&oacute; hơn 200 bạn trẻ đăng k&yacute; tham gia ngắm nguyệt thực v&agrave; CLB đ&atilde; tổ chức một bữa ăn nhẹ tại c&ocirc;ng vi&ecirc;n. Để kế hoạch xem nguyệt thực to&agrave;n phần diễn ra thuận lợi, CLB chuẩn bị 4 k&iacute;nh thi&ecirc;n văn học v&agrave; nhiều vật dụng bảo vệ mắt để phục vụ nhu cầu ngắm &ldquo;mặt trăng m&aacute;u&rdquo; của người d&acirc;n v&agrave; c&aacute;c bạn trẻ.</p>\r\n\r\n<p>Đến thời điểm 19h17, nhiều người c&oacute; mặt ở c&ocirc;ng vi&ecirc;n biển Đ&ocirc;ng đ&atilde; rất h&aacute;o hức khi nh&igrave;n thấy tr&ecirc;n bầu trời xuất hiện nguyệt thực một phần. L&uacute;c n&agrave;y mặt trăng c&oacute; một phần m&agrave;u đỏ xuất hiện khoảng hơn 10 ph&uacute;t.&nbsp;</p>\r\n\r\n<p>Phải đến 20h36 ph&uacute;t, hiện tượng pha nguyệt thực mới xuất hiện trở lại.Tuy nhi&ecirc;n, do thời tiết ở Đ&agrave; Nẵng kh&aacute; &acirc;m u, trời nhiều m&acirc;y v&agrave; đổ mưa n&ecirc;n nguyệt thực đ&atilde; biến mất ngay sau đ&oacute;. Đến thời điểm 21h30, nhiều người d&acirc;n, bạn trẻ Đ&agrave; Nẵng tỏ ra hụt hẫng khi kh&ocirc;ng nh&igrave;n thấy được hiện tượng nguyệt thực to&agrave;n phần như mong đợi.</p>\r\n\r\n<p><em>Ch&ugrave;m ảnh giới trẻ Đ&agrave; Nẵng theo d&otilde;i quan s&aacute;t nguyệt thực tại c&ocirc;ng vi&ecirc;n biển Đ&ocirc;ng tối 4/4:</em></p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/anh_1_ngay_tu_dau_gio_chieu_nhieu_ban_tre_da_tap_trung_tai_cong_vien_b_wieq.jpg_zpslargvfqo.jpg" /></p>\r\n\r\n<p style="text-align:center"><em>Ngay từ cuối giờ chiều, nhiều bạn trẻ đ&atilde; tập trung về c&ocirc;ng vi&ecirc;n biển Đ&ocirc;ng để đ&oacute;n xem nguyệt thực to&agrave;n phần.</em></p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/anh_2_4_kinh_thien_van_hoc_duoc_chuan_bi_pxkf.jpg_zpstxeglpm5.jpg" /></p>\r\n\r\n<p style="text-align:center"><em>K&iacute;nh thi&ecirc;n văn học được c&aacute;c th&agrave;nh vi&ecirc;n CLB Thi&ecirc;n văn học Đ&agrave; Nẵng chuẩn bị để phục vụ người xem.</em></p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/anh_3_cac_thanh_vien_clb_huong_dan_cach_xem_nguyet_fxxb.jpg_zpsrgj2wpnv.jpg" /></p>\r\n\r\n<p style="text-align:center"><em>Th&agrave;nh vi&ecirc;n CLB thi&ecirc;n văn học Đ&agrave; Nẵng đang hướng dẫn c&aacute;ch xem nguyệt thực to&agrave;n phần cho c&aacute;c bạn trẻ.</em></p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/anh_4_su_kien_thu_hut_rat_nhieu_em_nho_erpr.jpg_zpsflmvfk3c.jpg" /></p>\r\n\r\n<p style="text-align:center"><em>Sự kiện thu h&uacute;t sự hiếu kỳ của nhiều em nhỏ.</em>&nbsp;</p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/anh_5_gioi_tre_hao_huc_quan_sat_hien_tuong_mat_trang_mau_1__aapd.jpg_zpsczfdpvxf.jpg" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/anh_5_gioi_tre_hao_huc_quan_sat_hien_tuong_mat_trang_mau_2__txyw.jpg_zpszy6kqaqb.jpg" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/anh_5_gioi_tre_hao_huc_quan_sat_hien_tuong_mat_trang_mau_gfwv.jpg_zpsyp700ii4.jpg" /></p>\r\n\r\n<p style="text-align:center"><em>C&aacute;c bạn trẻ Đ&agrave; Nẵng h&aacute;o hức chờ đợi hiện tượng &ldquo;mặt trăng m&aacute;u&rdquo; hiếm thấy.</em></p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/anh_6_hien_tuong_trang_trang_truoc_luc_xuat_hien_nguyet_thuc_hxqz.jpg_zpsce20v5w8.jpg" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/anh_6_nguyet_thuc_mot_phan_zsal.jpg_zpsqnshrtlv.jpg" /></p>\r\n\r\n<p><em>Hiện tượng mặt trăng trắng trước l&uacute;c xuất hiện nguyệt thực.</em>&nbsp;</p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/anh_7_hen_tuong_nguyet_thuc_da_xuat_hien_truoc_do_vao_2h20_ngay_4_4_oblk.jpg_zpswesfcnm9.jpg" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/anh_7_hien_tuong_pha_nguyet_thuc_toi_4_4_2__jjwl.jpg_zpshaqpw6ya.jpg" /></p>\r\n\r\n<p style="text-align:center"><em>Hiện tượng nguyệt thực một phần v&agrave; pha nguyệt thực xuất hiện tối 4/4 tại c&ocirc;ng vi&ecirc;n biển Đ&ocirc;ng, Đ&agrave; Nẵng.</em></p>\r\n\r\n<p style="text-align:right"><em><strong>B&aacute;o&nbsp;Tiền Phong</strong></em></p>\r\n\r\n<p style="text-align:right"><em><a href="https://www.tienphong.vn/gioi-tre/gioi-tre-da-nang-hao-huc-khi-duoc-xem-trang-mau-843211.tpo" rel="nofollow" target="_blank">https://www.tienphong.vn/gioi-tre/gioi-tre-da-nang-hao-huc-khi-duoc-xem-trang-mau-843211.tpo</a></em></p>', '1492911726.jpg', 1, 4, 6, '2017-04-22 18:42:06', '2017-04-22 18:42:06', NULL),
(11, 'Sáng nay, người người chăm chú ngắm nhật thực', 'Hoạt động này diễn ra tại bãi biển đoạn Công viên Biển Đông, Q.Sơn Trà, TP.Đà Nẵng do Câu lạc bộ Thiên văn học TP.Đà Nẵng tổ chức.', '<p>Hoạt động n&agrave;y diễn ra tại b&atilde;i biển đoạn C&ocirc;ng vi&ecirc;n Biển Đ&ocirc;ng, Q.Sơn Tr&agrave;, TP.Đ&agrave; Nẵng do C&acirc;u lạc bộ Thi&ecirc;n văn học TP.Đ&agrave; Nẵng tổ chức.</p>\r\n\r\n<p>Thời tiết s&aacute;ng 9.3 c&ograve;n se lạnh, nắng nhẹ, hiện tượng xuất hiện v&agrave;o s&aacute;ng sớm n&ecirc;n mặt trời kh&ocirc;ng qu&aacute; gắt, rất thuận lợi cho việc quan s&aacute;t, thời gian nhật thực diễn ra tại TP.Đ&agrave; Nẵng gần 2 tiếng đồng hồ.</p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/img_6471_vxvu_zpshg5ixls4.jpg" /></p>\r\n\r\n<p style="text-align:center"><em>Hướng dẫn mọi người quan s&aacute;t nhật thực an to&agrave;n&nbsp;</em></p>\r\n\r\n<p><em><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/img_6448_vxaq_zps87savvud.jpg" /></em></p>\r\n\r\n<p style="text-align:center"><em>Quan s&aacute;t nhật thực bằng k&iacute;nh chuy&ecirc;n dụng</em></p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/img_6445_tjhm_zpsbvin7xpn.jpg" /></p>\r\n\r\n<p style="text-align:center"><em>Đ&oacute;n b&oacute;ng nhật thực v&agrave;o hộp giấy</em>&nbsp;</p>\r\n\r\n<p>Bạn Th&aacute;i Văn Lợi (năm 3 ng&agrave;nh C&ocirc;ng nghệ th&ocirc;ng tin, ĐH B&aacute;ch khoa Đ&agrave; Nẵng), Chủ nhiệm C&acirc;u lạc bộ Thi&ecirc;n văn học TP.Đ&agrave; Nẵng cho hay, đ&acirc;y l&agrave; nhật thực to&agrave;n phần nhưng ở Việt Nam chỉ xem được nhật thực một phần, tại Đ&agrave; Nẵng nhật thực bắt đầu l&uacute;c 6 giờ 46 ph&uacute;t, đạt cực đại l&uacute;c 7 giờ 42 ph&uacute;t, v&agrave; kết th&uacute;c l&uacute;c 8 giờ 44 ph&uacute;t, độ che phủ cực đại l&agrave; 36%.</p>\r\n\r\n<p>C&acirc;u lạc bộ Thi&ecirc;n văn học đ&atilde; chuẩn bị c&aacute;c thiết bị để th&agrave;nh vi&ecirc;n cũng như người d&acirc;n v&agrave; du kh&aacute;ch quan s&aacute;t trực tiếp v&agrave; gi&aacute;n tiếp một c&aacute;ch an to&agrave;n như ống nh&ograve;m, k&iacute;nh thi&ecirc;n văn, phương ph&aacute;p chiếu b&oacute;ng, k&iacute;nh nhật thực, k&iacute;nh thợ h&agrave;n&hellip;</p>\r\n\r\n<p><em><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/img_6474_pcws_zpsx725ef8t.jpg" /></em></p>\r\n\r\n<p style="text-align:center"><em>Đ&ocirc;i bạn trẻ người Hồng K&ocirc;ng chụp ảnh cưới tại b&atilde;i biển Đ&agrave; Nẵng cũng đ&oacute;n xem hiện tượng thi&ecirc;n nhi&ecirc;n kỳ th&uacute; n&agrave;y</em></p>\r\n\r\n<p><em><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/img_6442_pubw_zpsstywp37e.jpg" /></em></p>\r\n\r\n<p style="text-align:center"><em>Đ&ocirc;ng đảo bạn trẻ tham gia ch&agrave;o đ&oacute;n hiện tượng kỳ th&uacute;</em></p>\r\n\r\n<p><em>&nbsp;<img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/img_6465_aexm_zps0cm8qqlc.jpg" /></em></p>\r\n\r\n<p style="text-align:center"><em>Th&agrave;nh vi&ecirc;n CLB Thi&ecirc;n văn học điều chỉnh k&iacute;nh quan s&aacute;t</em></p>\r\n\r\n<p><em>&nbsp;<img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/img_6441_rseu_zpsmvhplv17.jpg" /></em></p>\r\n\r\n<p><em><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/img_6443_qhze_zpswsmswgpc.jpg" /></em></p>\r\n\r\n<p style="text-align:center"><em>Hướng dẫn mọi người quan s&aacute;t nhật thực</em>&nbsp;</p>\r\n\r\n<p>Lần gần nhất người d&acirc;n Đ&agrave; Nẵng xem được nhật thực l&agrave; từ 21.5.2012 (Thanh Ni&ecirc;n đ&atilde; th&ocirc;ng tin) v&agrave; nếu bỏ qua lần xem n&agrave;y, phải chờ đến 26.12.2019 nhật thực mới trở lại. Trong khi đ&oacute; nguyệt thực diễn ra thường xuy&ecirc;n hơn, nguyệt thực một phần sắp đến v&agrave;o th&aacute;ng 8.2017 v&agrave; nguyệt thực to&agrave;n phần v&agrave;o th&aacute;ng 1.2018.</p>\r\n\r\n<p>Sinh vi&ecirc;n Cải Thị Thanh Huyền (năm 3 ng&agrave;nh Kiến tr&uacute;c ĐH B&aacute;ch khoa) cho biết, đ&acirc;y l&agrave; lần đầu bạn được xem nhật thực d&ugrave; đ&atilde; y&ecirc;u th&iacute;ch thi&ecirc;n văn từ l&acirc;u, Huyền chia sẻ trước cảnh tượng thi&ecirc;n nhi&ecirc;n kỳ diệu n&agrave;y bạn muốn kh&aacute;m ph&aacute; thi&ecirc;n văn nhiều hơn nữa v&agrave; truyền niềm đam m&ecirc; đến với mọi người.</p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/0736_zarc_zpskaqk69pz.jpg" /></p>\r\n\r\n<p style="text-align:center"><em>L&uacute;c 7 giờ 36 ph&uacute;t</em></p>\r\n\r\n<p><em>&nbsp;<img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/0738_gffo_zpszwjct29d.jpg" /></em></p>\r\n\r\n<p style="text-align:center"><em>7 giờ 38 ph&uacute;t, chuẩn bị cực đại</em></p>\r\n\r\n<p><em>&nbsp;<img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/0742_kvog_zpsyxybck5w.jpg" /></em></p>\r\n\r\n<p style="text-align:center"><em>Nhật thực cực đại l&uacute;c 7 giờ 42 ph&uacute;t</em><em>&nbsp;</em></p>\r\n\r\n<p><em>&nbsp;<img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/0747_yzka_zpsv2iyuf9h.jpg" /></em></p>\r\n\r\n<p style="text-align:center"><em>7 giờ 47 ph&uacute;t</em></p>\r\n\r\n<p><em>&nbsp;<img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/0752_oysz_zpshugiplhw.jpg" /></em></p>\r\n\r\n<p style="text-align:center"><em>7 giờ 52 ph&uacute;t</em></p>\r\n\r\n<p><em><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/0810_cmft_zpsvh8c5a5l.jpg" /></em></p>\r\n\r\n<p style="text-align:center"><em>8 giờ 10 ph&uacute;t</em></p>\r\n\r\n<p><em>&nbsp;<img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/0816_hlqd_zpshxqadq4t.jpg" /></em></p>\r\n\r\n<p style="text-align:center"><em>8 giờ 16 ph&uacute;t</em><em>&nbsp;</em></p>\r\n\r\n<p style="text-align:right"><em><strong>Nguyễn T&uacute; - B&aacute;o Thanh Ni&ecirc;n</strong></em></p>\r\n\r\n<p style="text-align:right"><em><a href="http://thanhnien.vn/doi-song/sang-nay-nguoi-nguoi-cham-chu-ngam-nhat-thuc-676281.html" rel="nofollow" target="_blank">http://thanhnien.vn/doi-song/sang-nay-nguoi-nguoi-cham-chu-ngam-nhat-thuc-676281.html</a></em></p>', '1492911958.jpg', 1, 4, 6, '2017-04-22 18:45:58', '2017-04-22 18:45:58', NULL),
(12, 'Giới trẻ Đà Nẵng háo hức xem nhật thực', 'Sáng 9/3, những người yêu thích thiên văn tại Đà Nẵng đã có cơ hội được chiêm ngưỡng hiện tượng nhật thực một phần. Tại Công viên Biển Đông, từ sớm, CLB Thiên văn học Đà Nẵng đã có mặt và chuẩn bị các thiết bị ống kính, mắt kính thiên văn để sẵn sàng chờ đón thời khắc nhật thực xuất hiện với sự tham gia của 200 thành viên.', '<p>S&aacute;ng 9/3, những người y&ecirc;u th&iacute;ch thi&ecirc;n văn tại Đ&agrave; Nẵng đ&atilde; c&oacute; cơ hội được chi&ecirc;m ngưỡng hiện tượng nhật thực một phần.</p>\r\n\r\n<p>Tại C&ocirc;ng vi&ecirc;n Biển Đ&ocirc;ng, từ sớm, CLB Thi&ecirc;n văn học Đ&agrave; Nẵng đ&atilde; c&oacute; mặt v&agrave; chuẩn bị c&aacute;c thiết bị ống k&iacute;nh, mắt k&iacute;nh thi&ecirc;n văn để sẵn s&agrave;ng chờ đ&oacute;n thời khắc nhật thực xuất hiện với sự tham gia của 200 th&agrave;nh vi&ecirc;n.</p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/7_vuho_zpsgdqjxoof.jpg" /></p>\r\n\r\n<p style="text-align:center"><em>C&aacute;c bạn trẻ xem nhật thực qua ống nh&ograve;m</em></p>\r\n\r\n<p>Bạn Th&aacute;i Văn Lợi, trưởng nh&oacute;m CLB thi&ecirc;n văn học Đ&agrave; Nẵng cho biết, đ&atilde; chuẩn bị 2 k&iacute;nh thi&ecirc;n văn quan s&aacute;t gi&aacute;n tiếp, 2 ống k&iacute;nh thi&ecirc;n văn d&ugrave;ng k&iacute;nh lọc c&ugrave;ng nhiều k&iacute;nh nhật thực chuy&ecirc;n dụng, chảo nước pha mực, ống nh&ograve;m,...để phục vụ tối đa qu&aacute; tr&igrave;nh chi&ecirc;m ngưỡng khoảnh khắc nhật thực.</p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/8_jgfk_zps8s3gebnh.jpg" /></p>\r\n\r\n<p style="text-align:center"><em>Qua thau nước</em></p>\r\n\r\n<p>Tại Đ&agrave; Nẵng, từ thời điểm 6h46, bắt đầu xuất hiện nhật thực một phần. Đến thời điểm 7h42, hiện tượng nhật thực &quot;mặt trăng ăn mặt trời&quot; đạt cực đại ở mức 36%. V&agrave; kết th&uacute;c v&agrave;o thời điểm 8h44.</p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/10_ptkp_zpsux6qxnub.jpg" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/12_njpr_zpsn5pn06u3.jpg" /></p>\r\n\r\n<p style="text-align:center"><em>H&igrave;nh ảnh nhật thực tại Đ&agrave; Nẵng</em></p>\r\n\r\n<p>C&aacute;c bạn trẻ tỏ ra rất h&aacute;o hức v&igrave; l&acirc;u lắm rồi mới được chi&ecirc;m ngưỡng hiện tượng nhật thực.</p>\r\n\r\n<p style="text-align:right"><em><strong>Kh&aacute;nh Hồng</strong></em></p>\r\n\r\n<p style="text-align:right"><em><a href="http://dantri.com.vn/nhip-song-tre/gioi-tre-da-nang-hao-huc-xem-nhat-thuc-20160309105917385.htm" rel="nofollow" target="_blank">http://dantri.com.vn/nhip-song-tre/gioi-tre-da-nang-hao-huc-xem-nhat-thuc-20160309105917385.htm</a></em></p>', '1492912072.jpg', 1, 4, 6, '2017-04-22 18:47:52', '2017-04-22 18:47:52', NULL),
(13, 'Giới trẻ Đà Nẵng đổ xô ra biển đón nhật thực', 'Từ sáng sớm hôm nay, các thành viên CLB Thiên văn học Đà Nẵng cùng những người yêu mến thiên văn đã tới bãi biển xinh đẹp tại công viên Biển Đông để chiêm ngưỡng hiện tượng nhật thực một phần. Từ 5h30, gần 200 thành viên và CLB Thiên văn học Đà Nẵng đã chuẩn bị các thiết bị ống kính, mắt kính thiên văn để sẵn sàng chờ đón thời khắc nhật thực xuất hiện.', '<p><strong>TPO - Từ s&aacute;ng sớm h&ocirc;m nay, c&aacute;c th&agrave;nh vi&ecirc;n CLB Thi&ecirc;n văn học Đ&agrave; Nẵng c&ugrave;ng những người y&ecirc;u mến thi&ecirc;n văn đ&atilde; tới b&atilde;i biển xinh đẹp tại c&ocirc;ng vi&ecirc;n Biển Đ&ocirc;ng để chi&ecirc;m ngưỡng hiện tượng nhật thực một phần.</strong></p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/2_AQYM.JPG_zpsxvbkerez.jpg" /></p>\r\n\r\n<p style="text-align:center">&nbsp;<em>Gần 200 th&agrave;nh vi&ecirc;n CLB Thi&ecirc;n văn học Đ&agrave; Nẵng c&oacute; mặt từ l&uacute;c 5h30&#39; để chuẩn bị. Ảnh: Đ&agrave;o Phan</em></p>\r\n\r\n<p>Từ 5h30, gần 200 th&agrave;nh vi&ecirc;n v&agrave; CLB Thi&ecirc;n văn học Đ&agrave; Nẵng đ&atilde; chuẩn bị c&aacute;c thiết bị ống k&iacute;nh, mắt k&iacute;nh thi&ecirc;n văn để sẵn s&agrave;ng chờ đ&oacute;n thời khắc nhật thực xuất hiện.</p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/3_bltv.jpg_zpsxah9scy7.jpg" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/4_wtqm.jpg_zpsrnls9z14.jpg" /></p>\r\n\r\n<p style="text-align:center"><em>Sự kiện thu h&uacute;t nhiều người y&ecirc;u thi&ecirc;n văn trong nước v&agrave; du kh&aacute;ch</em></p>\r\n\r\n<p>Bạn Th&aacute;i Văn Lợi, trưởng nh&oacute;m CLB thi&ecirc;n văn học Đ&agrave; Nẵng cho biết đ&atilde; chuẩn bị 2 k&iacute;nh thi&ecirc;n văn quan s&aacute;t gi&aacute;n tiếp, 2 ống k&iacute;nh thi&ecirc;n văn d&ugrave;ng k&iacute;nh lọc c&ugrave;ng nhiều k&iacute;nh nhật thực chuy&ecirc;n dụng, chảo nước pha mực, ống nh&ograve;m.., để chi&ecirc;m ngưỡng khoảnh khắc nhật thực.</p>\r\n\r\n<p>V&agrave;o khoảng 6h46, tại Đ&agrave; Nẵng bắt đầu xuất hiện nhật thực một phần. Đến thời điểm 7h42, hiện tượng nhật thực đạt cực đại ở mức 36%. Hiện tượng nhật thực kết th&uacute;c tại Đ&agrave; Nẵng v&agrave;o l&uacute;c 8h44.</p>\r\n\r\n<p>Theo đại diện CLB Thi&ecirc;n văn học Đ&agrave; Nẵng cho biết, lần gần đ&acirc;y nhất hiện tượng nhật thực diễn ra tại Đ&agrave; Nẵng l&agrave; ng&agrave;y 21/5/2012. Sau h&ocirc;m nay, người y&ecirc;u thi&ecirc;n văn Đ&agrave; Nẵng sẽ phải đợi đến ng&agrave;y 26/12/2019 mới c&oacute; thể chi&ecirc;m ngưỡng hiện tượng n&agrave;y. Trong khi đ&oacute;, 8/2017, hiện tượng nguyệt thực một phần sẽ xuất hiện tại Việt Nam.</p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/5_rxkx.jpg_zpssf3p2ht5.jpg" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/6_utyg.jpg_zpsj0d8otal.jpg" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/7_vuho_zpsgdqjxoof.jpg" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/8_jgfk_zps8s3gebnh.jpg" /><em>&nbsp;</em></p>\r\n\r\n<p style="text-align:center"><em>Quan s&aacute;t nhật thực bằng mực tr&aacute;ng gương. Ảnh: Đ&agrave;o Phan</em>&nbsp;</p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/9_tbss_zpsfqu7xxqc.jpg" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/10_ptkp_zpsux6qxnub.jpg" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/11_espr_zpsetfhmipj.jpg" /></p>\r\n\r\n<p style="text-align:center"><em>Hiện tượng nhật thực một phần diễn ra tại Đ&agrave; Nẵng l&uacute;c 6h46</em></p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1067.photobucket.com/albums/u437/gemini4869/12_njpr_zpsn5pn06u3.jpg" /></p>\r\n\r\n<p style="text-align:center"><em>Nhật thực một phần đạt cực đại l&uacute;c 7h42 với tỷ lệ cực đại mặt trăng ăn mặt trời 36%. Ảnh: Đ&agrave;o Phan</em></p>\r\n\r\n<p><em>Đ&agrave;o Phan - B&aacute;o Tiền Phong</em></p>\r\n\r\n<p><em><a href="http://www.tienphong.vn/Gioi-Tre/gioi-tre-da-nang-do-xo-ra-bien-don-nhat-thuc-978754.tpo" rel="nofollow" target="_blank">http://www.tienphong.vn/Gioi-Tre/gioi-tre-da-nang-do-xo-ra-bien-don-nhat-thuc-978754.tpo</a></em></p>', '1492912201.jpg', 2, 4, 6, '2017-04-22 18:50:01', '2017-04-26 05:52:31', NULL),
(14, 'Chòm sao FA', 'Đây là một truyện ngắn từ Internet, được mình vẽ lại từng bức ảnh, cắt dán photoshop các kiểu ^^. Các bạn đọc và thưởng thức nhé. Chúc cả nhà buổi tối vui vẻ và ngắm sao cùng với cái bóng của chính mình.', '<p>Đ&acirc;y l&agrave; một truyện ngắn từ Internet, được m&igrave;nh vẽ lại từng bức ảnh, cắt d&aacute;n photoshop c&aacute;c kiểu ^^.</p>\r\n\r\n<p>C&aacute;c bạn đọc v&agrave; thưởng thức nh&eacute;.</p>\r\n\r\n<p>Ch&uacute;c cả nh&agrave; buổi tối vui vẻ v&agrave; ngắm sao c&ugrave;ng với c&aacute;i b&oacute;ng của ch&iacute;nh m&igrave;nh.</p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/TruyenCuoi_ChomSaoFA/1_zpsz7uqq16z.png" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/TruyenCuoi_ChomSaoFA/2_zpssbvdoued.png" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/TruyenCuoi_ChomSaoFA/3_zpsxrez64hj.png" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/TruyenCuoi_ChomSaoFA/4_zpsdmvjdggr.png" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/TruyenCuoi_ChomSaoFA/5_zpscnepancv.png" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/TruyenCuoi_ChomSaoFA/6_zpsivz2c1jd.png" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/TruyenCuoi_ChomSaoFA/7_zps5x4f8jlf.png" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/TruyenCuoi_ChomSaoFA/8_zpsvdk0cwht.png" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/TruyenCuoi_ChomSaoFA/9_zpsgirqt5jw.png" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/TruyenCuoi_ChomSaoFA/10_zpswdsxpfx3.png" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/TruyenCuoi_ChomSaoFA/11_zps1gjlaeif.png" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/TruyenCuoi_ChomSaoFA/12_zpsrpjsbmap.png" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/TruyenCuoi_ChomSaoFA/13_zpsyjnocmef.png" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/TruyenCuoi_ChomSaoFA/14_zpsn2cfptjw.png" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/TruyenCuoi_ChomSaoFA/15_zpsl2zfnwgi.png" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/TruyenCuoi_ChomSaoFA/16_zpsmnnuv6r9.png" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/TruyenCuoi_ChomSaoFA/17_zps6pkdilj6.png" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/TruyenCuoi_ChomSaoFA/18_zpsagftgcet.png" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/TruyenCuoi_ChomSaoFA/19_zpsechfq6kd.png" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/TruyenCuoi_ChomSaoFA/20_zpssdpr1j5c.png" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/TruyenCuoi_ChomSaoFA/21_zpsajrbbjb0.png" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/TruyenCuoi_ChomSaoFA/22_zpsvsjv9icu.png" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/TruyenCuoi_ChomSaoFA/23_zpsqlu6xvun.png" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/TruyenCuoi_ChomSaoFA/24_zpshqxyryoa.png" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/TruyenCuoi_ChomSaoFA/25_zpsz7atxgve.png" /></p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/TruyenCuoi_ChomSaoFA/26_zpsvicda8k7.png" /></p>\r\n\r\n<p style="text-align:right"><em>Th&aacute;i Lợi - DAC</em></p>', '1492912553.png', 1, 3, 6, '2017-04-22 18:55:40', '2017-04-22 18:55:53', NULL),
(15, 'Xác định chòm sao Bắc Miện và Dũng sĩ Hercules', 'Chòm sao Bắc Miện mang hình ảnh chiếc vòng hoa của Bacchus tặng cho nàng Ariadne. Chòm sao này với diện tích 179 độ vuông, rất nhỏ với vị trí 72/88 trong danh sách các chòm sao theo diện tích.', '<p>Ch&ograve;m sao Bắc Miện mang h&igrave;nh ảnh chiếc v&ograve;ng hoa của Bacchus tặng cho n&agrave;ng Ariadne. Ch&ograve;m sao n&agrave;y với diện t&iacute;ch 179 độ vu&ocirc;ng, rất nhỏ với vị tr&iacute; 72/88 trong danh s&aacute;ch c&aacute;c ch&ograve;m sao theo diện t&iacute;ch.</p>\r\n\r\n<p>Dũng sĩ Hercules l&agrave; con trai của thần Zeus. Ch&agrave;ng được biết đến với 12 kỳ c&ocirc;ng nổi tiếng v&agrave; c&aacute;c kh&oacute; khăn m&agrave; ch&agrave;ng gặp phải đều l&agrave; &acirc;m mưu của nữ thần Hera. Những ch&ograve;m sao c&oacute; truyền thuyết li&ecirc;n quan đến ch&agrave;ng Hercules gồm Leo, Hydra, Cancer, Sagittarius, Lupus, Aquila, Sagitta, Draco,&hellip; Ch&ograve;m sao Dũng sĩ Hercules với diện t&iacute;ch 1225 độ vu&ocirc;ng, lớn thứ 5 về diện t&iacute;ch trong danh s&aacute;ch 88 ch&ograve;m sao.</p>\r\n\r\n<p>Hai ch&ograve;m sao Bắc Miện v&agrave; Dũng sĩ Hercules rất kh&oacute; x&aacute;c định, v&igrave; vậy ch&uacute;ng ta c&oacute; thể x&aacute;c định hai ch&ograve;m sao n&agrave;y th&ocirc;ng qua hai ng&ocirc;i sao Arcturus (ch&ograve;m sao Mục Phu) v&agrave; Vega (ch&ograve;m sao Thi&ecirc;n Cầm).</p>\r\n\r\n<p>Trước ti&ecirc;n, bạn h&atilde;y x&aacute;c định hai ng&ocirc;i sao: Arcturus (bằng nh&oacute;m sao Bắc Đẩu) v&agrave; Vega (ng&ocirc;i sao s&aacute;ng nhất trong &ldquo;Tam gi&aacute;c m&ugrave;a h&egrave;&rdquo;).</p>\r\n\r\n<p>&ndash; Arcturus (tức Alpha Bootis) l&agrave; ng&ocirc;i sao s&aacute;ng nhất trong ch&ograve;m sao Mục Phu, v&agrave; l&agrave; ng&ocirc;i sao s&aacute;ng thứ tư tr&ecirc;n bầu trời đ&ecirc;m với độ s&aacute;ng biểu kiến l&agrave; -0,04, c&aacute;ch Tr&aacute;i Đất 37 năm &aacute;nh s&aacute;ng.Theo tiếng Hi Lạp, Arcturus c&oacute; nghĩa l&agrave; người theo d&otilde;i gấu, v&igrave; Mục Phu l&agrave; h&igrave;nh ảnh người thợ săn gấu, lu&ocirc;n theo d&otilde;i hai mẹ con gấu, tức l&agrave; ch&ograve;m sao Gấu lớn v&agrave; Gấu nhỏ.</p>\r\n\r\n<p>&ndash; Vega (tức Alpha Lyrae, Chức Nữ) l&agrave; ng&ocirc;i sao s&aacute;ng nhất ch&ograve;m sao Thi&ecirc;n Cầm v&agrave; l&agrave; ng&ocirc;i sao s&aacute;ng thứ năm tr&ecirc;n bầu trời đ&ecirc;m với độ s&aacute;ng biểu kiến l&agrave; 0, được lấy l&agrave;m mốc về độ s&aacute;ng biểu kiến c&aacute;c thi&ecirc;n thể. Vega c&aacute;ch Tr&aacute;i Đất 25 năm &aacute;nh s&aacute;ng.</p>\r\n\r\n<p>Sau đ&oacute; bạn sẽ kẻ một đoạn thẳng nối hai ng&ocirc;i sao s&aacute;ng n&agrave;y v&agrave; chia đoạn thẳng đ&oacute; th&agrave;nh 3 phần bằng nhau. Vị tr&iacute; 2 điểm chia đoạn thẳng n&agrave;y ch&iacute;nh l&agrave; nằm ngay ch&ograve;m sao Bắc Miện v&agrave; Dũng sĩ Hercules như h&igrave;nh vẽ.</p>\r\n\r\n<p><img alt="BacMien-DungSiHercules_zpshp7lihvj - Xác định chòm sao Bắc Miện và Dũng sĩ Hercules" src="http://i0.wp.com/i65.photobucket.com/albums/h220/ThaiVanLoiDN/BacMien-DungSiHercules_zpshp7lihvj.png?resize=960%2C625" /></p>\r\n\r\n<p style="text-align:right"><em>Th&aacute;i Lợi &ndash; DAC</em></p>', '1492912683.png', 2, 3, 6, '2017-04-22 18:58:03', '2017-04-26 05:47:49', NULL);
INSERT INTO `post` (`id`, `title`, `description`, `content`, `image`, `read_count`, `category_id`, `user_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(16, 'Chòm sao Thợ Săn và Bọ Cạp', 'Theo Thần thoại Hy Lạp, thợ săn Orion là thợ săn tài giỏi, nhưng rất ngạo mạn, tự cho mình là người thợ săn tài giỏi nhất, nên đã bị nữ thần Hera – vợ của thần Zeus sai con bọ cạp xuống và giết chết chàng thợ săn này. Con bọ cạp giết chết chàng và cũng bị chàng dùng chân giẫm chết. Thần Zeus thương tiếc Orion nên cho chàng lên bầu trời, và con bọ cạp kia cũng được đưa lên trời, chính là chòm sao Scorpius. Mỗi lần chòm sao Scorpius mọc lên thì chòm sao Orion lại lặn xuống như trốn tránh kẻ thù của mình.', '<p>Theo Thần thoại Hy Lạp, thợ săn Orion l&agrave; thợ săn t&agrave;i giỏi, nhưng rất ngạo mạn, tự cho m&igrave;nh l&agrave; người thợ săn t&agrave;i giỏi nhất, n&ecirc;n đ&atilde; bị nữ thần Hera &ndash; vợ của thần Zeus sai con bọ cạp xuống v&agrave; giết chết ch&agrave;ng thợ săn n&agrave;y. Con bọ cạp giết chết ch&agrave;ng v&agrave; cũng bị ch&agrave;ng d&ugrave;ng ch&acirc;n giẫm chết. Thần Zeus thương tiếc Orion n&ecirc;n cho ch&agrave;ng l&ecirc;n bầu trời, v&agrave; con bọ cạp kia cũng được đưa l&ecirc;n trời, ch&iacute;nh l&agrave; ch&ograve;m sao Scorpius. Mỗi lần ch&ograve;m sao Scorpius mọc l&ecirc;n th&igrave; ch&ograve;m sao Orion lại lặn xuống như trốn tr&aacute;nh kẻ th&ugrave; của m&igrave;nh.</p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1027.photobucket.com/albums/y339/nguyenle3181997/11232173_498868993598969_4925253920192010980_o_zpswmjbircp.png" /></p>\r\n\r\n<p>Ch&ograve;m sao Bọ Cạp, ch&ograve;m sao nổi bật trong m&ugrave;a h&egrave; với đường cong như bản đồ Việt Nam quen thuộc. T&acirc;m của con bọ cạp n&agrave;y l&agrave; ng&ocirc;i sao Antares, cũng l&agrave; ng&ocirc;i sao s&aacute;ng nhất trong ch&ograve;m sao n&agrave;y. Antares c&oacute; m&agrave;u đỏ đặc trưng, n&oacute; l&agrave; ng&ocirc;i sao s&aacute;ng thứ 16 tr&ecirc;n bầu trời đ&ecirc;m v&agrave; c&aacute;ch ch&uacute;ng ta khoảng 600 năm &aacute;nh s&aacute;ng.</p>\r\n\r\n<p>Ch&ograve;m sao Thợ săn Orion l&agrave; ch&ograve;m sao nổi bật của m&ugrave;a đ&ocirc;ng, l&agrave; ch&ograve;m sao ch&igrave;a kh&oacute;a để định hướng c&aacute;c ng&ocirc;i sao s&aacute;ng của nh&oacute;m sao Lục gi&aacute;c m&ugrave;a đ&ocirc;ng v&agrave; rất nhiều ng&ocirc;i sao kh&aacute;c.</p>\r\n\r\n<p><em>Th&aacute;i Lợi - DAC</em></p>', '1492912854.png', 1, 3, 6, '2017-04-22 19:00:54', '2017-04-22 19:00:54', NULL),
(17, 'Chòm sao Gấu Lớn và Gấu Nhỏ', 'Theo thần thoại Hy Lạp kể rằng, nàng Alisto là một cô gái đẹp, đem lòng yêu thần Zeus và giữa 2 người đã có 1 đứa con, đó là Acas. Khi nữ thần Hera (tức là vợ của thần Zeus) biết được, máu ghen của nàng đã nổi lên đã biết Alisto thành 1 con gấu cái. Con gấu – nàng Alisto hoảng sợ, vội vã chạy vào trong rừng.', '<p>Theo thần thoại Hy Lạp kể rằng, n&agrave;ng Alisto l&agrave; một c&ocirc; g&aacute;i đẹp, đem l&ograve;ng y&ecirc;u thần Zeus v&agrave; giữa 2 người đ&atilde; c&oacute; 1 đứa con, đ&oacute; l&agrave; Acas. Khi nữ thần Hera (tức l&agrave; vợ của thần Zeus) biết được, m&aacute;u ghen của n&agrave;ng đ&atilde; nổi l&ecirc;n đ&atilde; biết Alisto th&agrave;nh 1 con gấu c&aacute;i. Con gấu &ndash; n&agrave;ng Alisto hoảng sợ, vội v&atilde; chạy v&agrave;o trong rừng.</p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i65.photobucket.com/albums/h220/ThaiVanLoiDN/13933139_1665250590467789_2034598594_n_zpsgbh53cyt.jpeg" /></p>\r\n\r\n<p>Sau khoảng 15 năm kh&ocirc;ng c&oacute; bố mẹ ở b&ecirc;n cạnh l&uacute;c n&agrave;y th&igrave; đứa b&eacute; Acas ng&agrave;y n&agrave;o đ&atilde; trở th&agrave;nh 1 thợ săn giỏi, ch&agrave;ng đ&atilde; gặp mẹ m&igrave;nh trong h&igrave;nh dạng một con gấu v&agrave; đ&atilde; kh&ocirc;ng nhận ra mẹ m&igrave;nh v&agrave; vội bắn chết con gấu ấy.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thần Zeus tr&ecirc;n trời thấy thương cho 2 mẹ con n&ecirc;n cũng đ&atilde; đưa hai mẹ con Alisto v&agrave; Acas l&ecirc;n trời v&agrave; trở th&agrave;nh 2 ch&ograve;m sao: Gấu Lớn v&agrave; Gấu Nhỏ. Nữ thần Hera lại nổi m&aacute;u ghen lần 2 đ&atilde; nhờ anh m&igrave;nh ngăn cản kh&ocirc;ng cho 2 mẹ con xuống nước (xuống đường ch&acirc;n trời) v&agrave; chỉ chạy l&ograve;ng v&ograve;ng ở tr&ecirc;n bầu trời (ở nước c&oacute; vĩ độ cao như Hy Lạp th&igrave; hai ch&ograve;m sao n&agrave;y m&atilde;i m&atilde;i ở tr&ecirc;n bầu trời, kh&ocirc;ng bao lặn xuống).</p>\r\n\r\n<p>Ch&ograve;m sao Gấu Lớn gồm nhiều ng&ocirc;i sao s&aacute;ng, trong đ&oacute; 7 ng&ocirc;i sao nổi bật của n&oacute; c&oacute; h&igrave;nh d&aacute;ng giống như c&aacute;i ghế đẩu n&ecirc;n c&ograve;n gọi l&agrave; nh&oacute;m sao Bắc Đẩu, hay c&aacute;i g&agrave;u l&ograve;ng &ndash; C&aacute;i Muỗng Lớn (Big Dipper)</p>\r\n\r\n<p>Đ&acirc;y l&agrave; một trong những ch&ograve;m sao c&oacute; ứng dụng quan trọng nhất trong bầu trời. N&oacute; mang nhiều chức năng đặc biệt như: để t&igrave;m ra sao Bắc Cực (Polaris), để định vị c&aacute;c ch&ograve;m sao kh&aacute;c, để t&iacute;nh thời gian,&hellip;</p>\r\n\r\n<p>Tương tự nh&oacute;m sao Bắc Đẩu của ch&ograve;m sao Gấu Lớn, 7 ng&ocirc;i sao của ch&ograve;m sao Gấu Nhỏ v&agrave; cũng mang h&igrave;nh dạng c&aacute;i muỗng, nhưng nhỏ hơn v&agrave; người ta hay gọi n&oacute; l&agrave; C&aacute;i Muỗng Nhỏ (Little Dipper). Ng&ocirc;i sao Bắc Cực (Polaris) l&agrave; ng&ocirc;i sao cuối c&ugrave;ng của c&aacute;i muỗng nhỏ n&agrave;y.</p>\r\n\r\n<p style="text-align:right"><em>Th&aacute;i Lợi - DAC</em></p>', '1492913260.png', 1, 3, 6, '2017-04-22 19:07:40', '2017-04-22 19:07:40', NULL),
(18, 'Chòm sao Rắn Nhiều Đầu', 'Theo thần thoại Hy Lạp, dũng sĩ Hercules (con trai thần Zeus) đã thực hiện 12 chiến công của mình. Và một trong các chiến công đó là tiêu diệt con quái vật Hydra – một con rắn nhiều đầu và những cái đầu của nó đều bất tử. Vì vậy khi Hercules chém vào từng cái đầu của nó thì nó đều mọc trở lại.', '<p>Theo thần thoại Hy Lạp, dũng sĩ Hercules (con trai thần Zeus) đ&atilde; thực hiện 12 chiến c&ocirc;ng của m&igrave;nh. V&agrave; một trong c&aacute;c chiến c&ocirc;ng đ&oacute; l&agrave; ti&ecirc;u diệt con qu&aacute;i vật Hydra &ndash; một con rắn nhiều đầu v&agrave; những c&aacute;i đầu của n&oacute; đều bất tử. V&igrave; vậy khi Hercules ch&eacute;m v&agrave;o từng c&aacute;i đầu của n&oacute; th&igrave; n&oacute; đều mọc trở lại. V&igrave; vậy m&agrave; Iolaus (ch&aacute;u của Hercules) đ&atilde; gi&uacute;p sức cho ch&agrave;ng. Khi Hercules ch&eacute;m v&agrave;o từng c&aacute;i đầu của con qu&aacute;i vật Hydra th&igrave; người ch&aacute;u Iolaus đ&atilde; d&ugrave;ng đuốc đốt ch&aacute;y những c&aacute;i đầu bị đứt, l&agrave;m ch&uacute;ng kh&ocirc;ng thể mọc trở lại. Trong l&uacute;c đ&oacute; con cua &ndash; Cancer &ndash; bạn của con qu&aacute;i vật Hydra, đ&atilde; chạy đến cắp v&agrave;o ch&acirc;n của Hercules. Ngay lập tức, con cua n&agrave;y đ&atilde; bị dũng sĩ Hercules d&ugrave;ng ch&ugrave;y đập chết n&oacute;. Con cua Cancer bị chết, qu&aacute;i vật Hydra bị ti&ecirc;u diệt, ch&agrave;ng dũng sĩ Hercules nh&uacute;ng những mũi t&ecirc;n của ch&agrave;ng v&agrave;o d&ograve;ng m&aacute;u độc của Hydra v&agrave; những mũi t&ecirc;n của Hercules trở th&agrave;nh những mũi t&ecirc;n độc, kh&ocirc;ng một người n&agrave;o c&oacute; thể tho&aacute;t khỏi c&aacute;i chết khi tr&uacute;ng những mũi t&ecirc;n của ch&agrave;ng.</p>\r\n\r\n<p><img alt="Hình ảnh" src="http://i1027.photobucket.com/albums/y339/nguyenle3181997/11692523_502137153272153_8012973687717007292_n_zps23z4oyvw.jpg" /></p>\r\n\r\n<p>Ch&ograve;m sao Hydra l&agrave; một ch&ograve;m sao c&oacute; diện t&iacute;ch khoảng 1303 độ vu&ocirc;ng, n&oacute; l&agrave; ch&ograve;m sao c&oacute; diện t&iacute;ch lớn nhất tr&ecirc;n bầu trời. C&aacute;c bạn c&oacute; thể quan s&aacute;t n&oacute; r&otilde; nhất v&agrave;o m&ugrave;a xu&acirc;n, n&oacute; nằm gần ch&ograve;m sao Con Cua (Cancer), Con Quạ (Corvus). Con rắn n&agrave;y d&agrave;i v&agrave; rất ngoằn ngh&egrave;o, &iacute;t c&oacute; những ng&ocirc;i sao s&aacute;ng, v&igrave; vậy n&ecirc;n bạn h&atilde;y d&ugrave;ng bản đồ sao để x&aacute;c định r&otilde; n&oacute; nh&eacute;!</p>\r\n\r\n<p style="text-align:right"><em>Th&aacute;i Lợi - DAC</em></p>', '1492913796.png', 1, 3, 6, '2017-04-22 19:16:36', '2017-04-22 19:16:36', NULL),
(19, 'Hướng dẫn quan sát sao Bắc Cực', 'Polaris (Alpha Ursar Minoris) là ngôi sao sáng nhất trong chòm sao Tiểu Hùng (Ursa Minor). Hiện nay, Alpha Ursar Minoris là ngôi sao gần thiên cực Bắc nhất, nên nó được chọn là sao Bắc Cực.', '<p><strong>Polaris (Alpha Ursar Minoris) l&agrave; ng&ocirc;i sao s&aacute;ng nhất trong ch&ograve;m sao Tiểu H&ugrave;ng (Ursa Minor). Hiện nay, Alpha Ursar Minoris l&agrave; ng&ocirc;i sao gần thi&ecirc;n cực Bắc nhất, n&ecirc;n n&oacute; được chọn l&agrave; sao Bắc Cực.</strong></p>\r\n\r\n<p><img alt="polaris - Hướng dẫn quan sát sao Bắc Cực" src="http://i2.wp.com/2.bp.blogspot.com/-a6vU4qNpj88/UtOxp8ijk2I/AAAAAAAACWc/9wN-ufnV3p0/s640/polaris.jpg?w=600" /></p>\r\n\r\n<p style="text-align:center"><em>Sao Bắc Cực (Polaris) nằm trong ch&ograve;m sao Tiểu H&ugrave;ng. H&igrave;nh ảnh m&ocirc; phỏng bởi phần mềm Stellarium.</em></p>\r\n\r\n<p>Sao Bắc Cực c&aacute;ch ch&uacute;ng ta khoảng 430 năm &aacute;nh s&aacute;ng v&agrave; c&oacute; độ s&aacute;ng biểu kiến l&agrave; 2,02. Độ cao của sao Bắc Cực tr&ecirc;n thi&ecirc;n cầu phụ thuộc vĩ độ nơi ta đứng quan s&aacute;t.</p>\r\n\r\n<p>Do hiện tượng tiến động (hay c&ograve;n gọi l&agrave; tuế sai) n&ecirc;n Polaris (sao Bắc Cực hiện nay) sẽ kh&ocirc;ng c&ograve;n l&agrave; sao Bắc Cực m&atilde;i m&atilde;i. Trong qu&aacute; khứ, v&agrave;o khoảng năm 3000 TCN, sao Thuban của ch&ograve;m sao Thi&ecirc;n Long (Draco) đ&atilde; từng l&agrave; sao Bắc Cực. V&agrave; trong tương lai sẽ đến lượt sao Chức Nữ (Vega) trong ch&ograve;m sao Thi&ecirc;n Cầm (Lyra) l&agrave;m sao Bắc Cực v&agrave;o khoảng năm 14000.</p>\r\n\r\n<p><img alt="2 - Hướng dẫn quan sát sao Bắc Cực" src="http://i1.wp.com/1.bp.blogspot.com/-93dyx6Jxp3I/UtOdewFfqyI/AAAAAAAACUA/-3ORBUYv8Vc/s1600/2.png?w=600" /></p>\r\n\r\n<p style="text-align:center"><em>H&igrave;nh ảnh m&ocirc; phỏng sao Thuban năm 3000 TCN bởi phần mềm Stellarium.</em></p>\r\n\r\n<p><img alt="3 - Hướng dẫn quan sát sao Bắc Cực" src="http://i1.wp.com/2.bp.blogspot.com/-Ntg4XHStIOk/UtOd4_pms1I/AAAAAAAACUQ/j3LRoHbcCtg/s1600/3.png?w=600" /></p>\r\n\r\n<p style="text-align:center"><em>H&igrave;nh ảnh m&ocirc; phỏng sao Vega năm 14000 bởi phần mềm Stellarium.</em></p>\r\n\r\n<p>Hiện nay, sao Bắc Cực c&aacute;ch thi&ecirc;n cực Bắc khoảng 1 độ. N&oacute; c&agrave;ng ng&agrave;y, c&agrave;ng tiến dần đến thi&ecirc;n cực Bắc hơn v&agrave; đến năm 2100, n&oacute; chỉ c&aacute;ch thi&ecirc;n cực Bắc khoảng 0,5 độ.</p>\r\n\r\n<p><img alt="4 - Hướng dẫn quan sát sao Bắc Cực" src="http://i2.wp.com/3.bp.blogspot.com/-IbyJ89D0e88/UtOe-hBGurI/AAAAAAAACUc/2nfwcS8X2eE/s1600/4.png?w=600" /></p>\r\n\r\n<p style="text-align:center"><em>H&igrave;nh ảnh m&ocirc; phỏng sao Polaris v&agrave;o năm 2100 bởi phần mềm Stellarium.</em></p>\r\n\r\n<p>Tr&ecirc;n thi&ecirc;n cầu, ch&uacute;ng ta c&oacute; sao Bắc Cực, nhưng tại sao lại kh&ocirc;ng c&oacute; sao Nam Cực? Bởi v&igrave; gần thi&ecirc;n cực Nam nhất l&agrave; sao &sigma; Octantis thuộc ch&ograve;m sao Octans. Ng&ocirc;i sao n&agrave;y c&oacute; độ s&aacute;ng biểu kiến l&agrave; 5.42 v&agrave; rất kh&oacute; c&oacute; thể quan s&aacute;t bằng mắt thường.</p>\r\n\r\n<p><img alt="5 - Hướng dẫn quan sát sao Bắc Cực" src="http://i1.wp.com/3.bp.blogspot.com/-kYvQ4iTDi50/UtOf4XK2QSI/AAAAAAAACUo/zEFejVpnFr8/s1600/5.png?w=600" /></p>\r\n\r\n<p style="text-align:center"><em>H&igrave;nh ảnh m&ocirc; phỏng sao &sigma; Octantis bởi phần mềm Stellarium.</em></p>\r\n\r\n<p>Bởi v&igrave; sao Polaris rất gần thi&ecirc;n cực Bắc n&ecirc;n trong một năm, dường như ng&ocirc;i sao n&agrave;y kh&ocirc;ng di chuyển tr&ecirc;n thi&ecirc;n cầu v&agrave; c&aacute;c ng&ocirc;i sao kh&aacute;c tr&ecirc;n b&aacute;n thi&ecirc;n cầu Bắc như đang quay xung quanh n&oacute;. Người ta dựa v&agrave;o sao Bắc Cực để x&aacute;c định phương hướng. Mỗi khi nhận biết được sao Bắc Cực l&agrave; người ta biết ch&iacute;nh x&aacute;c hướng Bắc rồi từ đ&oacute; suy ra c&aacute;c hướng c&ograve;n lại.</p>\r\n\r\n<p>L&agrave;m sao để nhận biết sao Bắc Cực tr&ecirc;n bầu trời? C&oacute; rất nhiều c&aacute;ch, nhưng trong số đ&oacute;, c&oacute; ba c&aacute;ch được sử dụng rộng r&atilde;i hơn cả. Đ&oacute; l&agrave; d&ugrave;ng nh&oacute;m sao Bắc Đẩu trong ch&ograve;m sao Đại H&ugrave;ng (Ursa Major), d&ugrave;ng 5 ng&ocirc;i sao s&aacute;ng tạo th&agrave;nh chữ M trong ch&ograve;m sao Thi&ecirc;n Hậu (Cassiopeia) v&agrave; d&ugrave;ng nh&oacute;m sao Thập tự phương Bắc trong ch&ograve;m sao Thi&ecirc;n Nga (Cygnus).</p>\r\n\r\n<h4><strong>1. D&Ugrave;NG NH&Oacute;M SAO BIG DIPPER (C&Aacute;I MUỖNG LỚN).</strong></h4>\r\n\r\n<p>Nh&oacute;m sao n&agrave;y rất dễ nhận biết tr&ecirc;n bầu trời với 7 ng&ocirc;i sao nổi bật, m&agrave; văn h&oacute;a phương Đ&ocirc;ng hay gọi l&agrave; &ldquo;Thất tinh Bắc Đẩu&rdquo; tạo th&agrave;nh h&igrave;nh chiếc g&aacute;o m&uacute;c nước. Bạn d&ugrave;ng hai ng&ocirc;i sao Merak v&agrave; Dubhe để t&igrave;m sao Bắc Cực bằng c&aacute;ch kẻ một đoạn thẳng từ sao Merak đến sao Dubhe rồi k&eacute;o d&agrave;i đoạn thẳng nối hai sao ấy khoảng 5 lần, bạn sẽ gặp sao Bắc Cực.</p>\r\n\r\n<p><img alt="tumblr_inline_mzfb67wt8g1rft9ew - Hướng dẫn quan sát sao Bắc Cực" src="http://i1.wp.com/31.media.tumblr.com/64b70552ad48becc9dd58e46eb9f0eb8/tumblr_inline_mzfb67wt8g1rft9ew.jpg?w=600&amp;ssl=1" /></p>\r\n\r\n<p style="text-align:center"><em>D&ugrave;ng hai ng&ocirc;i sao Merak v&agrave; Dubhe để x&aacute;c định sao Bắc Cực. Minh họa bởi Stellarium.</em></p>\r\n\r\n<p>Đ&acirc;y l&agrave; c&aacute;ch được sử dụng phổ biến nhất để t&igrave;m sao Bắc Cực. C&aacute;c bạn lưu &yacute; rằng, tr&ecirc;n thi&ecirc;n cầu kh&ocirc;ng chỉ c&oacute; &ldquo;Thất tinh Bắc Đẩu&rdquo; m&agrave; c&ograve;n c&oacute; cả &ldquo;Lục tinh Nam Đẩu&rdquo; đấy nh&eacute;! V&igrave; vậy, c&aacute;c bạn đừng nhầm m&agrave; d&ugrave;ng &ldquo;Lục tinh Nam Đẩu&rdquo; để t&igrave;m sao Polaris nh&eacute;!</p>\r\n\r\n<p><img alt="7+copy - Hướng dẫn quan sát sao Bắc Cực" src="http://i2.wp.com/3.bp.blogspot.com/-I-Z55vxiBH0/UtOn9h1kZII/AAAAAAAACVs/Hq-3P2K9jxE/s1600/7+copy.jpg?w=600" /></p>\r\n\r\n<p style="text-align:center"><em>Nh&oacute;m sao &ldquo;Lục tinh Nam Đẩu&rdquo; trong ch&ograve;m sao Cung Thủ (Sagittarius). Minh họa bởi Stellarium.</em></p>\r\n\r\n<h4><strong>2. D&Ugrave;NG 5 NG&Ocirc;I SAO TẠO TH&Agrave;NH CHỮ M TRONG CH&Ograve;M SAO THI&Ecirc;N HẬU (CASSIOPEIA).</strong></h4>\r\n\r\n<p>Nh&oacute;m sao Bắc Đẩu v&agrave; ch&ograve;m sao Thi&ecirc;n Hậu đối xứng nhau qua sao Bắc Cực. Việt Nam l&agrave; quốc gia c&oacute; vĩ độ thấp, n&ecirc;n sao Bắc Cực cũng ở vị tr&iacute; thấp v&agrave; gần đường ch&acirc;n trời. V&igrave; thế n&ecirc;n ta kh&ocirc;ng thể đồng thời thấy cả nh&oacute;m sao Bắc Đẩu v&agrave; ch&ograve;m sao Thi&ecirc;n Hậu được. Khi Bắc Đẩu từ từ lặn xuống đường ch&acirc;n trời về hướng T&acirc;y Bắc th&igrave; dần dần, ch&ograve;m sao Thi&ecirc;n Hậu cũng mọc l&ecirc;n từ hướng Đ&ocirc;ng Bắc. Đ&acirc;y l&agrave; thời điểm th&iacute;ch hợp để ch&uacute;ng ta d&ugrave;ng ch&ograve;m sao Thi&ecirc;n Hậu x&aacute;c định sao Bắc Cực.</p>\r\n\r\n<p>Bạn h&atilde;y d&ugrave;ng ng&ocirc;i sao &gamma; Cas v&agrave; ng&ocirc;i sao Ruchbah, kẻ đường vu&ocirc;ng g&oacute;c tại ng&ocirc;i sao &gamma; Cas rồi k&eacute;o d&agrave;i khoảng 7 lần độ d&agrave;i đoạn thẳng nối hai sao n&agrave;y để t&igrave;m sao Bắc Cực.</p>\r\n\r\n<p><img alt="tumblr_inline_mzfb83nMrx1rft9ew - Hướng dẫn quan sát sao Bắc Cực" src="http://i2.wp.com/31.media.tumblr.com/c840f3fffd4d772235ddfb031c51ba42/tumblr_inline_mzfb83nMrx1rft9ew.jpg?w=600&amp;ssl=1" /></p>\r\n\r\n<p style="text-align:center"><em>D&ugrave;ng hai ng&ocirc;i sao &gamma; Cas v&agrave; Ruchbah để t&igrave;m sao Bắc Cực. Minh họa bởi Stellarium.</em></p>\r\n\r\n<h4><strong>3. D&Ugrave;NG NH&Oacute;M SAO &ldquo;THẬP TỰ PHƯƠNG BẮC&rdquo; TRONG CH&Ograve;M SAO THI&Ecirc;N NGA (CYGNUS).</strong></h4>\r\n\r\n<p>Thập tự phương Bắc (hay c&ograve;n gọi l&agrave; Bắc Thập Tự) l&agrave; nh&oacute;m sao nổi bật, c&oacute; h&igrave;nh một chữ Thập tr&ecirc;n thi&ecirc;n cầu Bắc. N&oacute; nằm trong ch&ograve;m sao Thi&ecirc;n Nga.</p>\r\n\r\n<p><img alt="10+copy - Hướng dẫn quan sát sao Bắc Cực" src="http://i0.wp.com/3.bp.blogspot.com/-T17SJU1ynd8/UtOmhsS4hZI/AAAAAAAACVY/Xa88zVAaUWA/s1600/10+copy.jpg?w=600" /></p>\r\n\r\n<p style="text-align:center"><em>Nh&oacute;m sao Thập tự phương Bắc. Minh họa bởi Stellarium.</em></p>\r\n\r\n<p>C&aacute;c bạn d&ugrave;ng sao Gienah v&agrave; sao Deneb (Deneb l&agrave; 1 trong 3 đỉnh của tam gi&aacute;c m&ugrave;a h&egrave;) của nh&oacute;m sao Thập tự phương Bắc để x&aacute;c định sao Bắc Cực bằng c&aacute;ch kẻ đường thẳng đi qua hai ng&ocirc;i sao n&agrave;y. Đường thẳng bạn vừa kẻ sẽ đi qua sao Bắc Cực.</p>\r\n\r\n<p><img alt="tumblr_inline_mzfc98rAme1rft9ew - Hướng dẫn quan sát sao Bắc Cực" src="http://i1.wp.com/31.media.tumblr.com/85c8ea0037127d932d4f7d410709c3f1/tumblr_inline_mzfc98rAme1rft9ew.jpg?w=600&amp;ssl=1" /></p>\r\n\r\n<p style="text-align:center"><em>D&ugrave;ng ng&ocirc;i sao Gienah v&agrave; Deneb để x&aacute;c định sao Bắc Cực. Minh họa bởi Stellarium.</em></p>\r\n\r\n<p>Thi&ecirc;n cầu Bắc c&oacute; Bắc Thập Tự, th&igrave; thi&ecirc;n cầu Nam cũng c&oacute; Nam Thập Tự. Đ&oacute; l&agrave; ch&ograve;m sao Crux, thường được gọi l&agrave; ch&ograve;m sao Nam T&agrave;o. Ch&ograve;m sao n&agrave;y d&ugrave;ng để x&aacute;c định hướng Nam.</p>\r\n\r\n<p><img alt="11+copy - Hướng dẫn quan sát sao Bắc Cực" src="http://i0.wp.com/3.bp.blogspot.com/-OaPSbwoyf-o/UtOlM5TsaCI/AAAAAAAACVM/FKOELW73T-k/s1600/11+copy.jpg?w=600" /></p>\r\n\r\n<p style="text-align:center"><em>Ch&ograve;m sao Nam Thập Tự (Crux). Minh họa bởi Stellarium.</em></p>\r\n\r\n<p>Tr&ecirc;n đ&acirc;y l&agrave; 3 c&aacute;ch phổ biến để x&aacute;c định sao Bắc Cực. C&aacute;c bạn x&aacute;c định được sao Bắc Cực rồi th&igrave; sẽ kh&ocirc;ng bị mất phương hướng đ&acirc;u nh&eacute;!</p>\r\n\r\n<p style="text-align:center"><em><strong>Th&aacute;i Văn Lợi &ndash; DAC</strong></em></p>', '1492913989.png', 2, 3, 6, '2017-04-22 19:19:49', '2017-04-26 05:54:31', NULL),
(20, 'Các tam giác, hình vuông và lục giác sao theo mùa', 'Hình vuông mùa Thu: Gồm ba ngôi sao của chòm sao Pegasus (Phi Mã): Scheat, Markab, Algenib, và Alpheratz của chòm sao Andromeda (Tiên Nữ)', '<h3>M&Ugrave;A XU&Acirc;N</h3>\r\n\r\n<p><strong>Tam gi&aacute;c m&ugrave;a Xu&acirc;n:</strong></p>\r\n\r\n<p>&ndash; Spica (Ch&ograve;m Virgo &ndash; Xử Nữ)</p>\r\n\r\n<p>&ndash; Denebola (Ch&ograve;m Leo &ndash; Sư Tử)</p>\r\n\r\n<p>&ndash; Arcturus (Ch&ograve;m Bootes &ndash; Mục Phu)</p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/thienvandanang.com%20-%20TamGiacMuaXuan_zpsnhm5rcev.png" /></p>\r\n\r\n<p style="text-align:center"><em>Tam gi&aacute;c m&ugrave;a xu&acirc;n.</em></p>\r\n\r\n<h3>M&Ugrave;A H&Egrave;</h3>\r\n\r\n<p><strong>Tam gi&aacute;c m&ugrave;a H&egrave;</strong></p>\r\n\r\n<p>&ndash; Vega (Chức Nữ) trong ch&ograve;m sao Lyra (Thi&ecirc;n Cầm)</p>\r\n\r\n<p>&ndash; Altair (Ngưu Lang) trong ch&ograve;m sao Aquila (Thi&ecirc;n Ưng)</p>\r\n\r\n<p>&ndash; Deneb trong ch&ograve;m sao Cygnus (Thi&ecirc;n Nga)</p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/thienvandanang.com%20-%20TamGiacMuaHe_zpsxvb39wkj.png" />&nbsp;</p>\r\n\r\n<p style="text-align:center"><em>Tam gi&aacute;c m&ugrave;a h&egrave;.</em></p>\r\n\r\n<h3>M&Ugrave;A THU</h3>\r\n\r\n<p><strong>H&igrave;nh vu&ocirc;ng m&ugrave;a Thu:</strong></p>\r\n\r\n<p>Gồm ba ng&ocirc;i sao của ch&ograve;m sao Pegasus (Phi M&atilde;): Scheat, Markab, Algenib, v&agrave; Alpheratz của ch&ograve;m sao Andromeda (Ti&ecirc;n Nữ)</p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/thienvandanang.com%20-%20HinhVuongMuaThu_zpsfmqu3vdu.png" /></p>\r\n\r\n<p style="text-align:center"><em>H&igrave;nh vu&ocirc;ng m&ugrave;a thu.</em></p>\r\n\r\n<h3>M&Ugrave;A Đ&Ocirc;NG</h3>\r\n\r\n<p><strong>Tam gi&aacute;c m&ugrave;a Đ&ocirc;ng</strong></p>\r\n\r\n<p>&ndash; Sirius (ch&ograve;m Canis Major &ndash; Đại Khuyển)</p>\r\n\r\n<p>&ndash; Procyon (ch&ograve;m Canis Minor &ndash; Tiểu Khuyển)</p>\r\n\r\n<p>&ndash; Betelgeuse (ch&ograve;m Orion &ndash; Thợ Săn)</p>\r\n\r\n<p><strong>Lục gi&aacute;c m&ugrave;a Đ&ocirc;ng</strong></p>\r\n\r\n<p>&ndash; Sirius (ch&ograve;m Canis Major &ndash; Đại Khuyển)</p>\r\n\r\n<p>&ndash; Procyon (ch&ograve;m Canis Minor &ndash; Tiểu Khuyển)</p>\r\n\r\n<p>&ndash; Pollux v&agrave; Castor (ch&ograve;m Gemini &ndash; Song Tử)</p>\r\n\r\n<p>&ndash; Capella (ch&ograve;m Auriga &ndash; Ngự Phu)</p>\r\n\r\n<p>&ndash; Aldebaran (ch&ograve;m Taurus &ndash; Kim Ngưu)</p>\r\n\r\n<p>&ndash; Rigel (ch&ograve;m Orion &ndash; Thợ Săn)</p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/thienvandanang.com%20-%20TamGiacLucGiacMuaDong_zpspzfn1n38.png" /></p>\r\n\r\n<p style="text-align:center"><em>Tam gi&aacute;c v&agrave; lục gi&aacute;c m&ugrave;a đ&ocirc;ng.</em></p>\r\n\r\n<p style="text-align:right"><em>Th&aacute;i Lợi - DAC</em></p>', '1492914176.png', 3, 3, 6, '2017-04-22 19:22:56', '2017-04-26 02:08:16', NULL),
(21, 'Bầu trời sao mùa đông', 'Lục giác mùa đông là nhóm sao nổi tiếng và nổi bật trên bầu trời sao mùa đông. Các bạn xem lần lượt album này theo thứ tự để quan sát các ngôi sao của nhóm sao này nhé', '<p>Lục gi&aacute;c m&ugrave;a đ&ocirc;ng l&agrave; nh&oacute;m sao nổi tiếng v&agrave; nổi bật tr&ecirc;n bầu trời sao m&ugrave;a đ&ocirc;ng. C&aacute;c bạn xem lần lượt album n&agrave;y theo thứ tự để quan s&aacute;t c&aacute;c ng&ocirc;i sao của nh&oacute;m sao n&agrave;y nh&eacute; :D.</p>\r\n\r\n<p>Lục gi&aacute;c m&ugrave;a đ&ocirc;ng: Nh&oacute;m sao gồm c&aacute;c ng&ocirc;i sao s&aacute;ng, tạo n&ecirc;n một h&igrave;nh lục gi&aacute;c tuyệt đẹp:</p>\r\n\r\n<p>- Sirius (ng&ocirc;i sao s&aacute;ng nhất ch&ograve;m sao Ch&oacute; Lớn)</p>\r\n\r\n<p>- Rigel (ng&ocirc;i sao s&aacute;ng nhất ch&ograve;m sao Thợ Săn)</p>\r\n\r\n<p>- Aldebaran (ng&ocirc;i sao s&aacute;ng nhất ch&ograve;m sao Kim Ngưu)</p>\r\n\r\n<p>- Capella (ng&ocirc;i sao s&aacute;ng nhất ch&ograve;m sao Ngự Phu)</p>\r\n\r\n<p>- Pollux v&agrave; Castor (hai ng&ocirc;i sao s&aacute;ng nhất ch&ograve;m sao Song Tử, Pollux s&aacute;ng hơn Castor)</p>\r\n\r\n<p>- Procyon (ng&ocirc;i sao s&aacute;ng nhất ch&ograve;m sao Ch&oacute; Nhỏ)</p>\r\n\r\n<p>Ngo&agrave;i ra c&ograve;n nh&oacute;m sao nổi tiếng kh&aacute;c l&agrave; tam gi&aacute;c m&ugrave;a đ&ocirc;ng, một h&igrave;nh tam gi&aacute;c đều, c&aacute;c bạn nh&eacute;:</p>\r\n\r\n<p>- Sirius (ng&ocirc;i sao s&aacute;ng nhất ch&ograve;m sao Ch&oacute; Lớn)</p>\r\n\r\n<p>- Procyon (ng&ocirc;i sao s&aacute;ng nhất ch&ograve;m sao Ch&oacute; Nhỏ)</p>\r\n\r\n<p>- Betelgeuse (ng&ocirc;i sao s&aacute;ng nh&igrave; ch&ograve;m sao Thợ Săn, k&eacute;m sao Rigel)</p>\r\n\r\n<p>Ngo&agrave;i ra m&igrave;nh c&ograve;n bonus th&ecirc;m v&agrave;i ch&ograve;m sao l&acirc;n cận nữa ^^.</p>\r\n\r\n<p>Lưu &yacute;: Trong b&agrave;i n&agrave;y khi n&oacute;i đến s&aacute;ng, độ s&aacute;ng,... th&igrave; m&igrave;nh n&oacute;i đến độ s&aacute;ng biểu kiến khi nh&igrave;n từ Tr&aacute;i Đất, chứ kh&ocirc;ng phải độ s&aacute;ng thực tế của ng&ocirc;i sao, c&aacute;c bạn nh&eacute;!</p>\r\n\r\n<p>---</p>\r\n\r\n<p>1 - Bầu trời đ&ecirc;m m&ugrave;a đ&ocirc;ng thật đẹp, c&aacute;c bạn nhỉ!</p>\r\n\r\n<p>C&aacute;c bạn h&atilde;y nh&igrave;n về hướng Đ&ocirc;ng nh&eacute;</p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/LucGiacMuaDong/1_zps3prbpofu.png" /></p>\r\n\r\n<p>2 - Trong những ng&ocirc;i sao s&aacute;ng nhất, bạn sẽ thấy ba ng&ocirc;i sao s&aacute;ng thẳng h&agrave;ng, c&aacute;ch đều, gần nhau, đ&oacute; l&agrave; ba ng&ocirc;i sao thắt lưng của ch&agrave;ng thợ săn Orion đấy! Gần đ&oacute; c&oacute; hai ng&ocirc;i sao s&aacute;ng kh&aacute;c, một m&agrave;u trắng xanh - sao Rigel v&agrave; ng&ocirc;i sao kia m&agrave;u đỏ - Betelgeuse. Đ&acirc;y l&agrave; ch&ograve;m sao Orion, kẻ th&ugrave; của con bọ cap - ch&iacute;nh l&agrave; ch&ograve;m sao Scorpius, bạn sẽ quan s&aacute;t tốt ch&ograve;m sao Bọ Cạp v&agrave;o m&ugrave;a h&egrave;. ^^ Trong ch&ograve;m sao Orion c&oacute; nhiều thi&ecirc;n thể đ&aacute;ng để quan s&aacute;t lắm đấy nh&eacute;, điển h&igrave;nh nhất l&agrave; tinh v&acirc;n Orion, M42 nh&eacute;! Bạn h&atilde;y quan s&aacute;t n&oacute; qua k&iacute;nh thi&ecirc;n văn.</p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/LucGiacMuaDong/2_zpsfxwa8opa.png" /></p>\r\n\r\n<p>3 - Đ&acirc;y l&agrave; h&igrave;nh ảnh của ch&agrave;ng thợ săn n&agrave;y</p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/LucGiacMuaDong/3_zpsehwnkghu.png" /></p>\r\n\r\n<p>4 - Ch&ograve;m sao n&agrave;y c&oacute; thể d&ugrave;ng để x&aacute;c định phương hướng đấy nh&eacute;, đầu lu&ocirc;n hướng Bắc, ch&acirc;n hướng về Nam. Nhưng c&oacute; bạn n&agrave;o thắc mắc l&agrave; tại sao b&ecirc;n phải l&agrave; T&acirc;y, m&agrave; kh&ocirc;ng phải l&agrave; Đ&ocirc;ng kh&ocirc;ng nhỉ. V&igrave; hướng tr&ecirc;n trời v&agrave; mặt đất ngược nhau m&agrave;, như tấm gương vậy :D. Tuy nhi&ecirc;n, c&aacute;ch x&aacute;c định phương hướng n&agrave;y th&igrave; m&igrave;nh &iacute;t d&ugrave;ng lắm, m&igrave;nh hay x&aacute;c định sao Bắc Cực rồi t&igrave;m hướng bắc cho ch&iacute;nh x&aacute;c hơn ^^.</p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/LucGiacMuaDong/4_zpspwza65zu.png" /></p>\r\n\r\n<p>5 - D&ugrave;ng ba ng&ocirc;i sao thẳng h&agrave;ng n&agrave;y, c&aacute;c bạn kẻ một đường thẳng qua n&oacute;, bạn gặp một ng&ocirc;i sao rất s&aacute;ng, đ&oacute; l&agrave; Sirius (hay gọi l&agrave; Thi&ecirc;n Lang), l&agrave; ng&ocirc;i sao s&aacute;ng nhất bầu trời đ&ecirc;m (tuy nhi&ecirc;n n&oacute; vẫn thua nhiều thi&ecirc;n thể kh&aacute;c: Mặt Trời, Mặt Trăng, Sao Kim, Sao Mộc,...), n&oacute; chỉ s&aacute;ng nhất trong số c&aacute;c ng&ocirc;i sao bầu trời đ&ecirc;m th&ocirc;i nh&eacute; ^^ nhiều bạn mới v&agrave;o nghề th&igrave; hay nhầm c&aacute;i n&agrave;y :D. Nguy&ecirc;n nh&acirc;n ch&iacute;nh l&agrave;m cho n&oacute; s&aacute;ng vậy v&igrave; n&oacute; gần, chỉ c&aacute;ch hơn 8 năm &aacute;nh s&aacute;ng th&ocirc;i, gần hơn nhiều so với c&aacute;c ng&ocirc;i sao kh&aacute;c (nhưng kh&ocirc;ng phải gần nhất nh&eacute;).</p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/LucGiacMuaDong/5_zpsimeafh5e.png" /></p>\r\n\r\n<p>6 - Đ&acirc;y l&agrave; h&igrave;nh ảnh ch&ograve;m sao Ch&oacute; Lớn (hay gọi l&agrave; Đại Khuyển)</p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/LucGiacMuaDong/6_zpsp4gpoapm.png" /></p>\r\n\r\n<p>7 - Kẻ về hướng ngược lại, bạn sẽ gặp ng&ocirc;i sao m&agrave;u đỏ - Aldebaran, ng&ocirc;i sao s&aacute;ng nhất của ch&ograve;m sao Kim Ngưu. Ch&ograve;m sao Kim Ngưu c&oacute; h&igrave;nh dạng như chữ Y, hay c&aacute;i cạng n&aacute; bắn d&acirc;y cao su. Trong ch&ograve;m sao n&agrave;y c&oacute; cụm sao Thất Nữ, Pleiades, M45 tuyệt đẹp, bạn h&atilde;y quan s&aacute;t n&oacute; qua k&iacute;nh thi&ecirc;n văn nh&eacute;, nh&igrave;n vi diệu lắm đ&oacute; ^^</p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/LucGiacMuaDong/7_zpstn5gn9c1.png" /></p>\r\n\r\n<p>8 - Đ&acirc;y l&agrave; h&igrave;nh ảnh của ch&ograve;m sao Kim Ngưu n&agrave;y</p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/LucGiacMuaDong/8_zpseva2mm1p.jpg" /></p>\r\n\r\n<p>9 - Kẻ đường thẳng qua hai vai của ch&agrave;ng thợ săn, bạn sẽ gặp Procyon, ng&ocirc;i sao s&aacute;ng nhất của ch&ograve;m sao Ch&oacute; Nhỏ. Ch&ograve;m sao Ch&oacute; Nhỏ hơi kh&oacute; h&igrave;nh dung một t&iacute;. Ch&oacute; Lớn v&agrave; Ch&oacute; Nhỏ l&agrave; hai ch&ograve;m sao mang h&igrave;nh ảnh của hai con ch&oacute; săn của ch&agrave;ng thợ săn Orion, đừng nhầm hai con ch&oacute; n&agrave;y với ch&ograve;m sao Ch&oacute; Săn, c&aacute;c bạn nh&eacute;.</p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/LucGiacMuaDong/9_zpsxhzyfdqo.png" /></p>\r\n\r\n<p>10 - Đ&acirc;y l&agrave; h&igrave;nh ảnh của ch&ograve;m sao Ch&oacute; Nhỏ.</p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/LucGiacMuaDong/10_zpsgdu9n9ot.jpg" /></p>\r\n\r\n<p>11 - H&atilde;y kẻ đường thẳng qua hai ng&ocirc;i sao s&aacute;ng nhất của Orion, từ Rigel s&aacute;ng nhất đến Betelgeuse s&aacute;ng nh&igrave;, hơi lệch một &iacute;t, bạn sẽ gặp hai ng&ocirc;i sao s&aacute;ng tương đương nhau, đ&oacute; l&agrave; Pollux v&agrave; Castor. Pollux l&agrave; anh, c&ograve;n Castor l&agrave; em. C&oacute; truyền thuyết n&oacute;i rằng đ&acirc;y l&agrave; hai anh em ruột, cũng c&oacute; truyền thuyết kh&aacute;c n&oacute;i rằng đ&acirc;y l&agrave; hai anh em c&ugrave;ng mẹ kh&aacute;c cha.</p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/LucGiacMuaDong/11_zpsecokvk56.png" /></p>\r\n\r\n<p>12 - Đ&acirc;y l&agrave; h&igrave;nh ảnh của ch&ograve;m sao Song Tử - Gemini, t&acirc;m điểm trận mưa sao băng Geminids thường ni&ecirc;n, với cực điểm thường hay diễn ra v&agrave;o ng&agrave;y 13-14/12 hằng năm.</p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/LucGiacMuaDong/12_zpsx3rynh1a.jpg" /></p>\r\n\r\n<p>13 - Kẻ từ thắt lưng ch&agrave;ng thợ săn l&ecirc;n đến đầu của ch&agrave;ng, bạn sẽ gặp sao Capella, s&aacute;ng nhất ch&ograve;m Ngự Phu. Ng&ocirc;i sao n&agrave;y hơi xa một t&iacute; so với c&aacute;c ng&ocirc;i sao kh&aacute;c.</p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/LucGiacMuaDong/13_zpspmu7awgl.png" /></p>\r\n\r\n<p>14 - Đ&acirc;y l&agrave; h&igrave;nh ảnh của ch&ograve;m sao Ngự Phu.</p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/LucGiacMuaDong/14_zpsc4wckyh5.jpg" /></p>\r\n\r\n<p>15 - N&agrave;o, h&atilde;y c&ugrave;ng quan s&aacute;t hai nh&oacute;m sao: Lục gi&aacute;c m&ugrave;a đ&ocirc;ng v&agrave; Tam gi&aacute;c m&ugrave;a đ&ocirc;ng nh&eacute;!</p>\r\n\r\n<p>Lục gi&aacute;c m&ugrave;a đ&ocirc;ng: Nh&oacute;m sao gồm c&aacute;c ng&ocirc;i sao s&aacute;ng, tạo n&ecirc;n một h&igrave;nh lục gi&aacute;c tuyệt đẹp:</p>\r\n\r\n<p>- Sirius (ng&ocirc;i sao s&aacute;ng nhất ch&ograve;m sao Ch&oacute; Lớn)</p>\r\n\r\n<p>- Rigel (ng&ocirc;i sao s&aacute;ng nhất ch&ograve;m sao Thợ Săn)</p>\r\n\r\n<p>- Aldebaran (ng&ocirc;i sao s&aacute;ng nhất ch&ograve;m sao Kim Ngưu)</p>\r\n\r\n<p>- Capella (ng&ocirc;i sao s&aacute;ng nhất ch&ograve;m sao Ngự Phu)</p>\r\n\r\n<p>- Pollux v&agrave; Castor (hai ng&ocirc;i sao s&aacute;ng nhất ch&ograve;m sao Song Tử, Pollux s&aacute;ng hơn Castor). Hai ng&ocirc;i sao n&agrave;y gần nhau, trong lục gi&aacute;c to tướng n&agrave;y th&igrave; c&aacute;c bạn tạm xem n&oacute; như l&agrave; 1 đi ^^</p>\r\n\r\n<p>- Procyon (ng&ocirc;i sao s&aacute;ng nhất ch&ograve;m sao Ch&oacute; Nhỏ)</p>\r\n\r\n<p>Ngo&agrave;i ra c&ograve;n nh&oacute;m sao nổi tiếng kh&aacute;c l&agrave; tam gi&aacute;c m&ugrave;a đ&ocirc;ng, một h&igrave;nh tam gi&aacute;c đều, c&aacute;c bạn nh&eacute;:</p>\r\n\r\n<p>- Sirius (ng&ocirc;i sao s&aacute;ng nhất ch&ograve;m sao Ch&oacute; Lớn)</p>\r\n\r\n<p>- Procyon (ng&ocirc;i sao s&aacute;ng nhất ch&ograve;m sao Ch&oacute; Nhỏ)</p>\r\n\r\n<p>- Betelgeuse (ng&ocirc;i sao s&aacute;ng nh&igrave; ch&ograve;m sao Thợ Săn, k&eacute;m sao Rigel)</p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/LucGiacMuaDong/15_zpscsyro5wy.png" /></p>\r\n\r\n<p>16 - Quay trở lại với ch&ograve;m sao Thợ Săn, ch&uacute;ng ta h&atilde;y c&ugrave;ng quan s&aacute;t một ch&ograve;m sao kh&aacute;c.</p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/LucGiacMuaDong/16_zpsdapgyqmr.png" /></p>\r\n\r\n<p>17 - Đ&oacute; l&agrave; ch&ograve;m sao Con Thỏ, n&oacute; nằm dưới ch&acirc;n ch&agrave;ng Orion. Ch&ograve;m sao n&agrave;y kh&ocirc;ng chứa ng&ocirc;i sao n&agrave;o của lục gi&aacute;c hay tam gi&aacute;c m&ugrave;a đ&ocirc;ng nh&eacute;.</p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/LucGiacMuaDong/17_zpsmrzmlonq.png" /></p>\r\n\r\n<p>18 - Đ&acirc;y l&agrave; h&igrave;nh ảnh của n&oacute; n&egrave;</p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/LucGiacMuaDong/18_zpstwzzwemv.png" /></p>\r\n\r\n<p>19 - Bonus th&ecirc;m một v&agrave;i ch&ograve;m sao kh&aacute;c.</p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/LucGiacMuaDong/19_zpsyhtkih2k.png" /></p>\r\n\r\n<p>20 - H&atilde;y nh&igrave;n về hướng bắc, bạn sẽ gặp 5 ng&ocirc;i sao xếp h&igrave;nh th&agrave;nh chữ M hay W, chữ E hay số 3, đ&oacute; l&agrave; 5 ng&ocirc;i sao s&aacute;ng nhất của ch&ograve;m sao Thi&ecirc;n Hậu. M&igrave;nh gọi n&oacute; l&agrave; 5 ng&ocirc;i sao s&aacute;ng nhất, v&igrave; mỗi ch&ograve;m sao (theo định nghĩa ch&ograve;m sao hiện đại) sẽ chiếm một khu vực diện t&iacute;ch, ng&ocirc;i sao, hoặc thi&ecirc;n thể n&agrave;o trong khu vực diện t&iacute;ch của ch&ograve;m sao n&agrave;o, th&igrave; thuộc ch&ograve;m sao đ&oacute; tức l&agrave; kh&ocirc;ng thể n&oacute;i ch&ograve;m sao n&agrave;o c&oacute; bao nhi&ecirc;u ng&ocirc;i sao được v&agrave; ngo&agrave;i ng&ocirc;i sao ra, th&igrave; ch&ograve;m sao c&ograve;n c&oacute; một số tinh v&acirc;n, thi&ecirc;n h&agrave; hoặc cụm sao,...</p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/LucGiacMuaDong/20_zpsst1ykcg8.png" /></p>\r\n\r\n<p>21 - Đ&acirc;y l&agrave; h&igrave;nh ảnh của ho&agrave;ng hậu Cassiopeia - ch&ograve;m sao Thi&ecirc;n Hậu.</p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/LucGiacMuaDong/21_zpszfea8s1g.png" /></p>\r\n\r\n<p>22 - H&atilde;y kẻ đường thẳng vu&ocirc;ng g&oacute;c với đường thẳng chứa ng&ocirc;i sao thứ ba v&agrave; tư, tại ng&ocirc;i sao thứ ba, bạn sẽ gặp sao Bắc Cực (Polaris), như trong h&igrave;nh vẽ. C&ograve;n ch&ograve;m sao Gấu Nhỏ th&igrave; c&aacute;c bạn để đến h&egrave; rồi coi lại n&oacute; nh&eacute;, giờ n&oacute; quay đầu xuống thấp n&ecirc;n kh&oacute; nh&igrave;n. V&agrave; ng&ocirc;i sao n&agrave;y chỉ gần như tuyệt đối hướng Bắc.</p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/LucGiacMuaDong/22_zpsdcfby5e2.png" /></p>\r\n\r\n<p>23 - Hướng Đ&ocirc;ng, gần nửa đ&ecirc;m l&agrave; thời gian th&iacute;ch hợp để quan s&aacute;t ch&ograve;m sao Sư Tử. Đầu n&oacute; đi trước, đu&ocirc;i đi sau. &quot;Sư Tử hướng về T&acirc;y&quot; - đ&acirc;y cũng l&agrave; c&aacute;ch x&aacute;c định phương hướng lu&ocirc;n, mặc d&ugrave; hơi lệch t&iacute;, v&agrave; kh&oacute; h&igrave;nh dung, kh&oacute; x&aacute;c định hướng cho lắm :v</p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/LucGiacMuaDong/23_zpsgfo3jn8x.png" /></p>\r\n\r\n<p>24 - H&atilde;y nh&igrave;n lại hai ch&ograve;m sao: Song Tử v&agrave; Sư Tử</p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/LucGiacMuaDong/24_zpsn3o7skza.png" /></p>\r\n\r\n<p>25 - Ch&ograve;m sao Con Cua, bạn c&oacute; thể x&aacute;c định n&oacute; bằng c&aacute;ch x&aacute;c định hai ch&ograve;m sao Song Tử v&agrave; Sư Tử. Ch&ograve;m sao Con Cua ở giữa hai ch&ograve;m sao n&agrave;y. Rất l&agrave; kh&oacute; để x&aacute;c định ch&ograve;m sao Con Cua v&igrave; n&oacute; kh&ocirc;ng c&oacute; ng&ocirc;i sao n&agrave;o nổi bật để ch&uacute;ng ta nhận biết cả. Th&ocirc;i đ&agrave;nh lựa ng&agrave;y trong, tại địa điểm th&iacute;ch hợp vậy :v.</p>\r\n\r\n<p><img alt="Hình ảnh" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/LucGiacMuaDong/25_zpsnfmbmxwi.png" /></p>\r\n\r\n<p>--------</p>\r\n\r\n<p>Vậy l&agrave; m&igrave;nh đ&atilde; kết th&uacute;c b&agrave;i hướng dẫn quan s&aacute;t n&agrave;y. Nếu đọc kỹ b&agrave;i n&agrave;y v&agrave; &aacute;p dụng theo, bạn c&oacute; thể x&aacute;c định được th&ecirc;m 11/88 ch&ograve;m sao rồi đấy ^^</p>\r\n\r\n<p style="text-align:right"><em>Th&aacute;i Lợi - DAC</em></p>', '1492914285.png', 2, 3, 6, '2017-04-22 19:24:45', '2017-04-26 01:56:15', NULL);
INSERT INTO `post` (`id`, `title`, `description`, `content`, `image`, `read_count`, `category_id`, `user_id`, `created_at`, `updated_at`, `deleted_at`) VALUES
(22, 'Bầu trời sao mùa hè', 'Mùa hè, bạn hãy nhìn về hướng Bắc, bạn sẽ thấy 7 ngôi sao tương đối sáng, xếp thành hình cái gàu múc nước, hay là dấu chấm hỏi ngược, đó chính là nhóm sao Big Dipper (Bắc Đẩu). Nhóm sao này thuộc chòm sao Ursa Major (Đại Hùng).', '<p><strong>Bầu trời sao m&ugrave;a h&egrave; nổi bật với ch&ograve;m sao Ursa Major (Đại H&ugrave;ng), sao Polaris (Bắc Cực), ch&ograve;m sao Scorpius (Bọ Cạp), dải Ng&acirc;n H&agrave; v&agrave; &ldquo;Tam gi&aacute;c m&ugrave;a h&egrave;&rdquo;.</strong></p>\r\n\r\n<p>M&ugrave;a h&egrave;, bạn h&atilde;y nh&igrave;n về hướng Bắc, bạn sẽ thấy 7 ng&ocirc;i sao tương đối s&aacute;ng, xếp th&agrave;nh h&igrave;nh c&aacute;i g&agrave;u m&uacute;c nước, hay l&agrave; dấu chấm hỏi ngược, đ&oacute; ch&iacute;nh l&agrave; nh&oacute;m sao Big Dipper (Bắc Đẩu). Nh&oacute;m sao n&agrave;y thuộc ch&ograve;m sao Ursa Major (Đại H&ugrave;ng). Từ nh&oacute;m sao n&agrave;y, c&aacute;c bạn h&atilde;y d&ugrave;ng bản đồ sao để x&aacute;c định ch&ograve;m sao Ursa Major (Đại H&ugrave;ng) nh&eacute;. Nh&oacute;m sao Bắc Đẩu l&agrave; ch&igrave;a kh&oacute;a để c&aacute;c bạn nhận biết c&aacute;c ng&ocirc;i sao nổi bật tr&ecirc;n bầu trời. Khoảng 21h v&agrave;o buổi tối giữa th&aacute;ng 6, bạn sẽ thấy nh&oacute;m sao n&agrave;y ở hướng T&acirc;y Bắc, cao khoảng từ 25 độ đến 50 độ tr&ecirc;n bầu trời.</p>\r\n\r\n<p><img alt="Ursa%20Major%20copy_zps4ig2oaxq - Hướng dẫn quan sát bầu trời sao mùa hè" src="http://i1.wp.com/i65.photobucket.com/albums/h220/ThaiVanLoiDN/BauTroiSaoMuaHe/Ursa%20Major%20copy_zps4ig2oaxq.png?resize=980%2C803" /></p>\r\n\r\n<p style="text-align:center"><em>Ch&ograve;m sao Ursa Major (Đại H&ugrave;ng)</em></p>\r\n\r\n<p>Trước hết l&agrave; sao Polaris (Bắc Cực). Bạn h&atilde;y vẽ một đường thẳng từ sao Merak đến sao Dubhe rồi k&eacute;o d&agrave;i đoạn thẳng nối hai sao n&agrave;y khoảng 5 lần, bạn sẽ gặp sao Polaris (Bắc Cực) như h&igrave;nh vẽ dưới đ&acirc;y. Sao Polaris (Bắc Cực) rất gần thi&ecirc;n cực bắc n&ecirc;n trong một năm, ng&ocirc;i sao n&agrave;y dường như kh&ocirc;ng thay đổi vị tr&iacute; của n&oacute; tr&ecirc;n thi&ecirc;n cầu, v&agrave; đ&ecirc;m n&agrave;o bạn cũng c&oacute; thể thấy được n&oacute;. Sao Polaris l&agrave; ng&ocirc;i sao s&aacute;ng nhất trong ch&ograve;m sao Ursa Minor (Tiểu H&ugrave;ng). C&aacute;c bạn dựa v&agrave;o bản đồ sao để x&aacute;c định ch&ograve;m sao Ursa Minor rồi tiếp đến l&agrave; ch&ograve;m sao Draco (Thi&ecirc;n Long) nh&eacute;!</p>\r\n\r\n<p><img alt="Polaris_zpszt7k7qgr - Hướng dẫn quan sát bầu trời sao mùa hè" src="http://i0.wp.com/i65.photobucket.com/albums/h220/ThaiVanLoiDN/BauTroiSaoMuaHe/Polaris_zpszt7k7qgr.png?resize=980%2C733" /></p>\r\n\r\n<p style="text-align:center"><em>D&ugrave;ng nh&oacute;m sao Bắc Đẩu để x&aacute;c định sao Bắc Cực</em></p>\r\n\r\n<p>Sau đ&oacute; l&agrave; hai ng&ocirc;i sao s&aacute;ng Arcturus v&agrave; Spica. Theo đường cong của 5 ng&ocirc;i sao trong nh&oacute;m sao Bắc Đẩu, bạn sẽ gặp sao Arcturus của ch&ograve;m sao Bo&ouml;tes (Mục Phu) rồi sau đ&oacute; sẽ l&agrave; sao Spica của ch&ograve;m sao Virgo (Xử Nữ). Sao Arcturus l&agrave; ng&ocirc;i sao s&aacute;ng thứ 4 tr&ecirc;n bầu trời đ&ecirc;m, trong khi đ&oacute; sao Spica th&igrave; đứng thứ 15. Khoảng 21h v&agrave;o buổi tối giữa th&aacute;ng 6, bạn sẽ thấy sao Arcturus nằm gần thi&ecirc;n đỉnh c&ograve;n sao Spica ở hướng T&acirc;y &ndash; Nam, cao khoảng 55 độ tr&ecirc;n bầu trời.</p>\r\n\r\n<p><img alt="Arcturus%20-%20Spica%20copy_zps4env8lxt - Hướng dẫn quan sát bầu trời sao mùa hè" src="http://i0.wp.com/i65.photobucket.com/albums/h220/ThaiVanLoiDN/BauTroiSaoMuaHe/Arcturus%20-%20Spica%20copy_zps4env8lxt.png?resize=980%2C737" /></p>\r\n\r\n<p style="text-align:center"><em>D&ugrave;ng Bắc Đẩu để x&aacute;c định sao Arcturus, Spica</em><em>&nbsp;</em></p>\r\n\r\n<p>Tiếp đến, bạn h&atilde;y d&ugrave;ng hai ng&ocirc;i sao Megrez v&agrave; Phad để x&aacute;c định sao Regulus &ndash; ng&ocirc;i sao s&aacute;ng nhất trong ch&ograve;m sao Leo (Sư Tử). Khoảng 21h v&agrave;o buổi tối giữa th&aacute;ng 6, bạn sẽ thấy sao Regulus ở hướng T&acirc;y, cao khoảng 20 độ tr&ecirc;n bầu trời.</p>\r\n\r\n<p><img alt="Big%20Dipper%20-%20Regulus%20copy_zpsvemzf2wi - Hướng dẫn quan sát bầu trời sao mùa hè" src="http://i1.wp.com/i65.photobucket.com/albums/h220/ThaiVanLoiDN/BauTroiSaoMuaHe/Big%20Dipper%20-%20Regulus%20copy_zpsvemzf2wi.png?resize=980%2C740" /></p>\r\n\r\n<p style="text-align:center"><em>D&ugrave;ng Bắc Đẩu để x&aacute;c định sao Regulus</em><em>&nbsp;</em></p>\r\n\r\n<p>Sau khi đ&atilde; t&igrave;m ra sao Regulus th&igrave; bạn h&atilde;y x&aacute;c định ch&ograve;m sao Leo (Sư Tử) đi nh&eacute;. C&aacute;i đu&ocirc;i của con sư tử n&agrave;y ch&iacute;nh l&agrave; sao Denebola, ng&ocirc;i sao s&aacute;ng thứ hai trong ch&ograve;m sao Leo. N&oacute; c&ugrave;ng với sao Arcturus v&agrave; sao Spica tạo th&agrave;nh một tam gi&aacute;c đều, v&agrave; người ta gọi n&oacute; l&agrave; &ldquo;tam gi&aacute;c m&ugrave;a xu&acirc;n&rdquo;. &nbsp;Tuy nhi&ecirc;n, c&oacute; nhiều t&agrave;i liệu th&igrave; n&oacute;i rằng, &ldquo;tam gi&aacute;c m&ugrave;a xu&acirc;n&rdquo; gồm sao Arcturus, sao Spica v&agrave; sao Regulus.</p>\r\n\r\n<p><img alt="TG%20mua%20Xuan%20copy_zps10uogl9o - Hướng dẫn quan sát bầu trời sao mùa hè" src="http://i1.wp.com/i65.photobucket.com/albums/h220/ThaiVanLoiDN/BauTroiSaoMuaHe/TG%20mua%20Xuan%20copy_zps10uogl9o.png?resize=980%2C735" /></p>\r\n\r\n<p style="text-align:center"><em>&ldquo;Tam gi&aacute;c m&ugrave;a xu&acirc;n&rdquo;</em></p>\r\n\r\n<p>Khi &ldquo;tam gi&aacute;c m&ugrave;a xu&acirc;n&rdquo; dần tiến về hướng T&acirc;y th&igrave; &ldquo;tam gi&aacute;c m&ugrave;a h&egrave;&rdquo; dần xuất hiện ở hướng đ&ocirc;ng. Bạn h&atilde;y nh&igrave;n về hướng Đ&ocirc;ng v&agrave; quan s&aacute;t nh&oacute;m sao n&agrave;y. Bạn h&atilde;y nh&igrave;n về dải Ng&acirc;n H&agrave; vắt ngang tr&ecirc;n bầu trời, ba ng&ocirc;i sao s&aacute;ng tạm th&agrave;nh &ldquo;tam gi&aacute;c m&ugrave;a h&egrave;&rdquo;: sao Vega (Chức Nữ) s&aacute;ng nhất trong ch&ograve;m sao Lyra (Thi&ecirc;n Cầm), sao Altair (Ngưu Lang) s&aacute;ng nhất trong ch&ograve;m sao Aquila (Đại B&agrave;ng) v&agrave; sao Deneb (Thi&ecirc;n T&acirc;n) s&aacute;ng nhất trong ch&ograve;m sao Cygnus (Thi&ecirc;n Nga). Sao Vega (Chức Nữ) l&agrave; ng&ocirc;i sao s&aacute;ng thứ năm tr&ecirc;n bầu trời đ&ecirc;m, trong khi đ&oacute;, sao Altair (Ngưu Lang) th&igrave; đứng &nbsp;thứ 11 v&agrave; sao Deneb (Thi&ecirc;n T&acirc;n) th&igrave; đứng thứ 19. Sao Altair v&agrave; sao Vega nằm ở hai b&ecirc;n dải Ng&acirc;n H&agrave; như c&acirc;u chuyện ngăn c&aacute;ch t&igrave;nh y&ecirc;u Ngưu Lang &ndash; Chức Nữ. Trong th&aacute;ng 6, khoảng l&uacute;c 21h th&igrave; &ldquo;tam gi&aacute;c m&ugrave;a h&egrave;&rdquo; nằm gần đường ch&acirc;n trời, v&igrave; vậy n&ecirc;n nếu muốn quan s&aacute;t n&oacute; dễ d&agrave;ng hơn th&igrave; bạn h&atilde;y thức đến nửa đ&ecirc;m hoặc đợi đến giữa th&aacute;ng 7 nh&eacute;!</p>\r\n\r\n<p><img alt="Tam%20giac%20He%20copy%20copy%20copy_zpsvar03wtf - Hướng dẫn quan sát bầu trời sao mùa hè" src="http://i0.wp.com/i65.photobucket.com/albums/h220/ThaiVanLoiDN/BauTroiSaoMuaHe/Tam%20giac%20He%20copy%20copy%20copy_zpsvar03wtf.png?resize=980%2C735" /></p>\r\n\r\n<p style="text-align:center"><em>&ldquo;Tam gi&aacute;c m&ugrave;a h&egrave;&rdquo;</em></p>\r\n\r\n<p>Sau đ&oacute;, h&atilde;y d&ugrave;ng thử kiểm tra sao Polaris (Bắc Cực) bằng c&aacute;ch kẻ đường thẳng đi qua sao Gienah v&agrave; sao Deneb của ch&ograve;m sao Cygnus (Thi&ecirc;n Nga) nh&eacute;!</p>\r\n\r\n<p><img alt="Cynus%20-%20Polaris%20copy_zpsznnhni92 - Hướng dẫn quan sát bầu trời sao mùa hè" src="http://i0.wp.com/i65.photobucket.com/albums/h220/ThaiVanLoiDN/BauTroiSaoMuaHe/Cynus%20-%20Polaris%20copy_zpsznnhni92.png?resize=980%2C735" /></p>\r\n\r\n<p style="text-align:center"><em>Kẻ đường thẳng đi qua sao Gienah v&agrave; sao Deneb, đường thẳng n&agrave;y sẽ đi qua sao Polaris (sao Bắc Cực).</em><em>&nbsp;</em></p>\r\n\r\n<p>Sau đ&oacute;, bạn h&atilde;y nh&igrave;n ch&ograve;m sao Scorpius (Bọ Cạp) ở hướng Đ&ocirc;ng Nam. Ch&ograve;m sao n&agrave;y nổi bật với đường cong như bản đồ Việt Nam quen thuộc. T&acirc;m của con bọ cạp n&agrave;y l&agrave; ng&ocirc;i sao Antares, cũng l&agrave; ng&ocirc;i sao s&aacute;ng nhất trong ch&ograve;m sao n&agrave;y. Antares c&oacute; m&agrave;u đỏ đặc trưng, n&oacute; l&agrave; ng&ocirc;i sao s&aacute;ng thứ 16 tr&ecirc;n bầu trời đ&ecirc;m v&agrave; c&aacute;ch ch&uacute;ng ta khoảng 600 năm &aacute;nh s&aacute;ng. Theo Thần thoại Hy Lạp, thợ săn Orion l&agrave; thợ săn t&agrave;i giỏi, nhưng rất ngạo mạn, tự cho m&igrave;nh l&agrave; người thợ săn t&agrave;i giỏi nhất, n&ecirc;n đ&atilde; bị nữ thần Hera &ndash; vợ của thần Zeus sai con bọ cạp xuống v&agrave; giết chết ch&agrave;ng thợ săn n&agrave;y. Con bọ cạp giết chết ch&agrave;ng v&agrave; cũng bị ch&agrave;ng d&ugrave;ng ch&acirc;n giẫm chết. Thần Zeus thương tiếc Orion n&ecirc;n cho ch&agrave;ng l&ecirc;n bầu trời, v&agrave; con bọ cạp kia cũng được đưa l&ecirc;n trời, ch&iacute;nh l&agrave; ch&ograve;m sao Scorpius. Mỗi lần ch&ograve;m sao Scorpius mọc l&ecirc;n th&igrave; ch&ograve;m sao Orion lại lặn xuống như trốn tr&aacute;nh kẻ th&ugrave; của m&igrave;nh. Khoảng 21h v&agrave;o buổi tối giữa th&aacute;ng 6, bạn sẽ thấy ch&ograve;m sao Bọ Cạp ở hướng Đ&ocirc;ng &ndash; Nam, cao khoảng từ 20 độ đến 52 độ tr&ecirc;n bầu trời.</p>\r\n\r\n<p><img alt="Scorpius%20copy_zps5dtwf0fh - Hướng dẫn quan sát bầu trời sao mùa hè" src="http://i2.wp.com/i65.photobucket.com/albums/h220/ThaiVanLoiDN/BauTroiSaoMuaHe/Scorpius%20copy_zps5dtwf0fh.png?resize=977%2C1024" /></p>\r\n\r\n<p style="text-align:center"><em>Ch&ograve;m sao Scorpius (Bọ Cạp)</em></p>\r\n\r\n<p>Ph&iacute;a b&ecirc;n dưới ch&ograve;m sao Scorpius (Bọ Cạp) l&agrave; ch&ograve;m sao Sagittarius (Cung Thủ). Ch&ograve;m sao n&agrave;y mang h&igrave;nh dạng của một nh&acirc;n m&atilde;, đầu người, m&igrave;nh ngựa. Ch&ograve;m sao n&agrave;y c&oacute; những ng&ocirc;i sao s&aacute;ng tạo th&agrave;nh nh&oacute;m sao The Teapot (Ấm Tr&agrave;). Tuy nhi&ecirc;n, nếu bạn để &yacute; kĩ th&igrave; ch&ograve;m sao n&agrave;y c&ograve;n c&oacute; những ng&ocirc;i sao tạo th&agrave;nh h&igrave;nh c&aacute;i g&aacute;o m&uacute;c nước đấy nh&eacute;! V&igrave; vậy, nếu bạn nh&igrave;n thấy h&igrave;nh một c&aacute;i g&agrave;o m&uacute;c nước ở hướng Nam th&igrave; bạn đừng nhầm n&oacute; với nh&oacute;m sao Big Dipper (Bắc Đẩu) nh&eacute;! Khoảng 21h v&agrave;o buổi tối giữa th&aacute;ng 6, ch&ograve;m sao Cung Thủ nằm gần đường ch&acirc;n trời, v&igrave; vậy n&ecirc;n cũng như &ldquo;tam gi&aacute;c m&ugrave;a h&egrave;&rdquo;, nếu muốn quan s&aacute;t n&oacute; dễ d&agrave;ng hơn th&igrave; bạn h&atilde;y thức đến nửa đ&ecirc;m hoặc đợi đến giữa th&aacute;ng 7 nh&eacute;!</p>\r\n\r\n<p><img alt="Scorpius%20-%20Sagittarius%20copy_zpsrmdu7dan - Hướng dẫn quan sát bầu trời sao mùa hè" src="http://i0.wp.com/i65.photobucket.com/albums/h220/ThaiVanLoiDN/BauTroiSaoMuaHe/Scorpius%20-%20Sagittarius%20copy_zpsrmdu7dan.png?resize=980%2C959" /></p>\r\n\r\n<p style="text-align:center"><em>Scorpius (Bọ Cạp) v&agrave; Sagittarius (Cung Thủ)</em><em>&nbsp;</em></p>\r\n\r\n<p>Ngay tr&ecirc;n đầu ch&ograve;m sao Scorpius ch&iacute;nh l&agrave; ch&ograve;m sao Libra (Thi&ecirc;n B&igrave;nh). Ch&ograve;m Libra mang h&igrave;nh d&aacute;ng của một chiếc c&acirc;n, n&oacute; l&agrave; ch&ograve;m sao Ho&agrave;ng đạo nằm giữa ch&ograve;m sao Scorpius v&agrave; Virgo (Xử Nữ). Bạn h&atilde;y dựa v&agrave;o ch&ograve;m sao Scorpius để x&aacute;c định n&oacute; nh&eacute;.</p>\r\n\r\n<p><img alt="Untitled-1%20copy_zpspqaepval - Hướng dẫn quan sát bầu trời sao mùa hè" src="http://i0.wp.com/i65.photobucket.com/albums/h220/ThaiVanLoiDN/BauTroiSaoMuaHe/Untitled-1%20copy_zpspqaepval.png?resize=980%2C966" /></p>\r\n\r\n<p style="text-align:center"><em>X&aacute;c định ch&ograve;m Libra bằng ch&ograve;m sao Scorpius.</em><em>&nbsp;</em></p>\r\n\r\n<p>B&ecirc;n cạnh ch&ograve;m sao Scorpius (Bọ Cạp) l&agrave; ch&ograve;m sao Ophiuchus (X&agrave; Phu). Đ&acirc;y ch&iacute;nh l&agrave; ch&ograve;m sao Ho&agrave;ng đạo thứ 13, do đường Ho&agrave;ng đạo đi qua ch&ograve;m sao n&agrave;y. Ch&ograve;m sao n&agrave;y chia đ&ocirc;i ch&ograve;m sao Serpens (Con Rắn) th&agrave;nh hai phần ri&ecirc;ng biệt. Serpens l&agrave; ch&ograve;m sao duy nhất tr&ecirc;n bầu trời bị chia th&agrave;nh 2 phần t&aacute;ch rời.</p>\r\n\r\n<p><img alt="Scorpius%20-%20Ophiuchus%20copy_zpsxqtt8tvc - Hướng dẫn quan sát bầu trời sao mùa hè" src="http://i0.wp.com/i65.photobucket.com/albums/h220/ThaiVanLoiDN/BauTroiSaoMuaHe/Scorpius%20-%20Ophiuchus%20copy_zpsxqtt8tvc.png?resize=980%2C736" /></p>\r\n\r\n<p style="text-align:center"><em>Scorpius (Bọ Cạp), Ophiuchus (X&agrave; Phu) v&agrave; Serpens (Con Rắn).</em></p>\r\n\r\n<p><img alt="Ecliptic%20copy_zpse5btvqkd - Hướng dẫn quan sát bầu trời sao mùa hè" src="http://i0.wp.com/i65.photobucket.com/albums/h220/ThaiVanLoiDN/BauTroiSaoMuaHe/Ecliptic%20copy_zpse5btvqkd.png?resize=980%2C815" /></p>\r\n\r\n<p style="text-align:center"><em><em>C&aacute;c ch&ograve;m sao Ho&agrave;ng đạo.</em>&nbsp;</em></p>\r\n\r\n<p>Sau khi đ&atilde; x&aacute;c định được ch&ograve;m sao Scorpius (Bọ Cạp), Ophiuchus (X&agrave; Phu) v&agrave; Sagittarius (Cung Thủ) rồi, c&aacute;c bạn h&atilde;y x&aacute;c định đường Ho&agrave;ng đạo nh&eacute;! Đường Ho&agrave;ng đạo đi qua &nbsp;c&aacute;c ch&ograve;m sao n&agrave;y đấy.<em>&nbsp;</em></p>\r\n\r\n<p>Bạn h&atilde;y nh&igrave;n về hướng Nam nh&eacute;, trong số rất nhiều ng&ocirc;i sao chi ch&iacute;t, bạn h&atilde;y để &yacute; những ng&ocirc;i sao s&aacute;ng nhất. Ch&ograve;m sao Crux (Nam Thập Tự) c&oacute; h&igrave;nh dấu cộng, c&oacute; hai ng&ocirc;i sao s&aacute;ng nhất l&agrave; Acrux v&agrave; Mimosa. Acrux l&agrave; ng&ocirc;i sao s&aacute;ng thứ 14 tr&ecirc;n bầu trời đ&ecirc;m c&ograve;n Mimosa th&igrave; xếp hạng thứ 20. Bạn c&oacute; thể quan s&aacute;t ch&ograve;m sao n&agrave;y v&agrave;o khoảng giữa th&aacute;ng 6 sau khi Mặt Trời lặn cho đến khoảng 22h30. Ph&iacute;a tr&ecirc;n Crux l&agrave; ch&ograve;m sao Centaurus (Nh&acirc;n M&atilde;). Cả hai ch&ograve;m sao Sagittarius (Cung Thủ) v&agrave; Centaurus (Nh&acirc;n M&atilde;) đều mang h&igrave;nh d&aacute;ng đầu người m&igrave;nh ngựa. Tuy nhi&ecirc;n, ch&ograve;m sao Sagittarius lại thường được gọi l&agrave; &ldquo;Nh&acirc;n M&atilde;&rdquo; trong khi ch&ograve;m Centaurus lại thường được gọi l&agrave; &ldquo;B&aacute;n Nh&acirc;n M&atilde;&rdquo;. Cả hai t&ecirc;n thường gọi n&agrave;y đều sai. Ch&ograve;m sao Sagittarius l&agrave; một ch&ograve;m sao Ho&agrave;ng Đạo (tức l&agrave; Mặt Trời sẽ đi qua ch&ograve;m sao n&agrave;y v&agrave;o một khoảng thời gian trong năm, cụ thể hơn l&agrave; ng&agrave;y 18/12 đến ng&agrave;y 18/01, theo c&aacute;ch nh&igrave;n biểu kiến từ Tr&aacute;i Đất). Trong khi đ&oacute;, ch&ograve;m sao Centaurus th&igrave; kh&ocirc;ng phải l&agrave; ch&ograve;m sao Ho&agrave;ng đạo, v&agrave; Mặt Trời sẽ kh&ocirc;ng đi qua ch&ograve;m sao n&agrave;y. Hai ng&ocirc;i sao s&aacute;ng nhất của ch&ograve;m sao Centaurus l&agrave; Rigil Kentaurus v&agrave; Hadar. Ng&ocirc;i sao Rigil Kentaurus l&agrave; ng&ocirc;i sao s&aacute;ng thứ 3 tr&ecirc;n bầu trời đ&ecirc;m (sau Sirius v&agrave; Canopus), trong khi đ&oacute; th&igrave; Hadar xếp thứ 10. Trong một khu vực nhỏ tr&ecirc;n thi&ecirc;n cầu lại c&oacute; đến 4 ng&ocirc;i sao s&aacute;ng thuộc hai ch&ograve;m sao Crux v&agrave; Centaurus.</p>\r\n\r\n<p><img alt="Centaurus%20-%20Crux%20copy_zpsdlhwh5i4 - Hướng dẫn quan sát bầu trời sao mùa hè" src="http://i0.wp.com/i65.photobucket.com/albums/h220/ThaiVanLoiDN/BauTroiSaoMuaHe/Centaurus%20-%20Crux%20copy_zpsdlhwh5i4.png?resize=980%2C738" /></p>\r\n\r\n<p style="text-align:center"><em>Crux (Nam Thập Tự) v&agrave; Centaurus (Nh&acirc;n M&atilde;)</em></p>\r\n\r\n<p>Bầu trời sao m&ugrave;a h&egrave; rất đẹp, c&aacute;c bạn h&atilde;y ngắm sao ở những v&ugrave;ng qu&ecirc; để thấy được vẻ đẹp của bầu trời h&egrave; nh&eacute;!</p>\r\n\r\n<p><img alt="Thang%206%20A%20copy_zpsqt78cmsg - Hướng dẫn quan sát bầu trời sao mùa hè" src="http://i2.wp.com/i65.photobucket.com/albums/h220/ThaiVanLoiDN/BauTroiSaoMuaHe/Thang%206%20A%20copy_zpsqt78cmsg.png?resize=980%2C937" /></p>\r\n\r\n<p style="text-align:center"><em>Bản đồ sao th&aacute;ng 6, l&uacute;c 22h tại Đ&agrave; Nẵng</em></p>\r\n\r\n<p style="text-align:right"><em><em>Th&aacute;i Văn Lợi - DAC</em></em></p>', '1492914419.png', 2, 3, 6, '2017-04-22 19:26:59', '2017-04-26 01:54:08', NULL),
(23, 'Bầu trời sao mùa thu', 'Mùa hè vừa rồi, chúng ta đã được chiêm ngưỡng “Tam giác mùa hè” tuyệt đẹp được tạo thành bởi ba ngôi sao Ngưu Lang, Chức Nữ và Thiên Tân lần lượt thuộc ba chòm sao Đại Bàng, Thiên Cầm và Thiên Nga rồi đúng không nào! Những ngày thu này, hình tam giác đặc biệt ấy vẫn xuất hiện từ giữa bầu trời và lặn dần về phía tây.', '<p>Bầu trời sao m&ugrave;a thu nổi bật với ch&ograve;m sao Phi M&atilde;, c&aacute;c ch&ograve;m sao ho&agrave;ng gia v&agrave; c&aacute;c ch&ograve;m sao ho&agrave;ng đạo như Ma Kết, Bảo B&igrave;nh, Song Ngư,&hellip;</p>\r\n\r\n<h3>1 &ndash; TAM GI&Aacute;C M&Ugrave;A H&Egrave;</h3>\r\n\r\n<p>M&ugrave;a h&egrave; vừa rồi, ch&uacute;ng ta đ&atilde; được chi&ecirc;m ngưỡng &ldquo;Tam gi&aacute;c m&ugrave;a h&egrave;&rdquo; tuyệt đẹp được tạo th&agrave;nh bởi ba ng&ocirc;i sao Ngưu Lang, Chức Nữ v&agrave; Thi&ecirc;n T&acirc;n lần lượt thuộc ba ch&ograve;m sao Đại B&agrave;ng, Thi&ecirc;n Cầm v&agrave; Thi&ecirc;n Nga rồi đ&uacute;ng kh&ocirc;ng n&agrave;o! Những ng&agrave;y thu n&agrave;y, h&igrave;nh tam gi&aacute;c đặc biệt ấy vẫn xuất hiện từ giữa bầu trời v&agrave; lặn dần về ph&iacute;a t&acirc;y.</p>\r\n\r\n<p><img alt="Bầu trời sao mùa thu" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/stellarium-004%20copy_zps43itzreb.png" /></p>\r\n\r\n<p style="text-align:center"><em>Tam gi&aacute;c m&ugrave;a h&egrave;</em></p>\r\n\r\n<h3>2 &ndash; C&Aacute;C CH&Ograve;M SAO HO&Agrave;NG GIA</h3>\r\n\r\n<p>B&acirc;y giờ ch&uacute;ng ta sẽ t&igrave;m hiểu một số ch&ograve;m sao đặc trưng của m&ugrave;a thu v&agrave; trước mắt l&agrave; c&aacute;c ch&ograve;m sao ho&agrave;ng gia nh&eacute;! Đ&oacute; l&agrave; c&aacute;c ch&ograve;m sao: Thi&ecirc;n Vương (vua), Thi&ecirc;n Hậu (ho&agrave;ng hậu), Ti&ecirc;n Nữ (c&ocirc;ng ch&uacute;a) v&agrave; Anh Ti&ecirc;n (ph&ograve; m&atilde;, tức l&agrave; chồng của Ti&ecirc;n Nữ).</p>\r\n\r\n<p>Bạn h&atilde;y nh&igrave;n l&ecirc;n bầu trời ph&iacute;a bắc, trong số nhiều ng&ocirc;i sao mờ ấy, bạn h&atilde;y ch&uacute; &yacute; đến c&aacute;c ng&ocirc;i sao tạo với nhau th&agrave;nh h&igrave;nh chữ M. Đ&oacute; l&agrave; c&aacute;c ng&ocirc;i sao s&aacute;ng trong ch&ograve;m sao Thi&ecirc;n Hậu đấy!</p>\r\n\r\n<p><img alt="Bầu trời sao mùa thu" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/stellarium-005%20copy_zpszsxczx9s.png" /></p>\r\n\r\n<p style="text-align:center"><em>Ch&ograve;m sao Thi&ecirc;n Hậu</em></p>\r\n\r\n<p>Ch&uacute;ng ta đ&atilde; biết c&aacute;ch nhận biết phương hướng bằng nh&oacute;m sao Bắc Đẩu để t&igrave;m ng&ocirc;i sao Bắc Cực rồi đ&uacute;ng kh&ocirc;ng n&agrave;o! Vậy giờ khi nh&oacute;m sao Bắc Đẩu đ&atilde; lặn xuống, ch&uacute;ng ta sẽ để cho nh&oacute;m sao Bắc Đẩu nghỉ ngơi v&agrave; d&ugrave;ng nh&oacute;m sao Thi&ecirc;n Hậu để thay thế nhiệm vụ đ&oacute; của n&oacute;.</p>\r\n\r\n<p>Để dễ nhớ, ch&uacute;ng ta c&ugrave;ng nhau quy ước thứ tự năm ng&ocirc;i sao n&agrave;y l&agrave; 1, 2, 3, 4, 5 theo thứ tự từ tr&aacute;i qua phải như trong h&igrave;nh vẽ dưới đ&acirc;y nh&eacute;! Sau đ&oacute;, bạn h&atilde;y kẻ một đường thẳng vu&ocirc;ng g&oacute;c với đường thẳng chứa ng&ocirc;i sao thứ 3 v&agrave; 4, đường thẳng n&agrave;y sẽ đi ngang qua sao Bắc Cực.</p>\r\n\r\n<p><img alt="Bầu trời sao mùa thu" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/stellarium-006%20copy_zpsspcu5mbl.png" /></p>\r\n\r\n<p style="text-align:center"><em>D&ugrave;ng ch&ograve;m sao Thi&ecirc;n Hậu để x&aacute;c định sao Bắc Cực.</em></p>\r\n\r\n<p>Ng&ocirc;i sao Bắc Cực l&agrave; ng&ocirc;i sao hầu như đứng y&ecirc;n tr&ecirc;n bầu trời, cả ban ng&agrave;y n&oacute; vẫn ở vị tr&iacute; đ&oacute; v&agrave; tất cả c&aacute;c ng&ocirc;i sao kh&aacute;c tr&ecirc;n bầu trời đều quay xung quanh ng&ocirc;i sao n&agrave;y. Để dễ h&igrave;nh dung, ch&uacute;ng ta xem lại quả địa cầu, khi ch&uacute;ng ta quay quả địa cầu th&igrave; cực bắc v&agrave; cực nam sẽ đứng y&ecirc;n một chỗ c&ograve;n c&aacute;c điểm kh&aacute;c sẽ quay xung quanh đường thẳng nối hai cực n&agrave;y.</p>\r\n\r\n<p><img alt="Bầu trời sao mùa thu" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/31030_zpsawn9fwcz.png" /></p>\r\n\r\n<p style="text-align:center"><em>Khi quả địa cầu quay, cực bắc v&agrave; cực nam của n&oacute; vẫn đứng y&ecirc;n.</em></p>\r\n\r\n<p>Tiếp đến l&agrave; ch&ograve;m sao Thi&ecirc;n Vương, tức l&agrave; &ocirc;ng vua, chồng của Thi&ecirc;n Hậu. Ch&ograve;m sao n&agrave;y c&oacute; h&igrave;nh dạng giống một ng&ocirc;i nh&agrave; bị lộn ngược m&aacute;i. Bạn h&atilde;y kẻ đường thẳng từ ng&ocirc;i sao thứ hai đến ng&ocirc;i sao thứ nhất của vị ho&agrave;ng hậu, bạn sẽ gặp ng&ocirc;i sao Alderamin của &ocirc;ng vua n&agrave;y đấy!</p>\r\n\r\n<p><img alt="Bầu trời sao mùa thu" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/stellarium-010%20copy_zpsaczryeic.png" /></p>\r\n\r\n<p style="text-align:center"><em>X&aacute;c định ch&ograve;m sao Thi&ecirc;n Vương bằng ch&ograve;m sao Thi&ecirc;n Hậu.</em></p>\r\n\r\n<p>Một ch&ograve;m sao kh&aacute; nổi bật, gi&uacute;p bạn x&aacute;c định hai ch&ograve;m sao ho&agrave;ng gia c&ograve;n lại Ti&ecirc;n Nữ v&agrave; Anh Ti&ecirc;n, đ&oacute; l&agrave; ch&ograve;m sao mang h&igrave;nh ảnh con ngựa c&oacute; c&aacute;nh &ndash; Pegasus (ch&ograve;m sao Phi M&atilde;). Kẻ một đường thẳng từ ng&ocirc;i sao thứ 3 đến ng&ocirc;i sao thứ 2 của Thi&ecirc;n Hậu, bạn sẽ gặp một h&igrave;nh vu&ocirc;ng kh&aacute; nổi bật, h&igrave;nh vu&ocirc;ng n&agrave;y gồm ba ng&ocirc;i sao của ch&ograve;m sao Phi M&atilde; v&agrave; một ng&ocirc;i sao của ch&ograve;m sao Ti&ecirc;n Nữ. Sau đ&oacute; bạn h&atilde;y x&aacute;c định ch&ograve;m sao Phi M&atilde; n&agrave;y nh&eacute;!</p>\r\n\r\n<p><img alt="Bầu trời sao mùa thu" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/stellarium-007%20copy_zps0eqwj370.png" /></p>\r\n\r\n<p style="text-align:center"><em>D&ugrave;ng ch&ograve;m sao Thi&ecirc;n Hậu để x&aacute;c định ch&ograve;m sao Phi M&atilde;.</em></p>\r\n\r\n<p>Sau khi x&aacute;c định được ch&ograve;m sao Phi M&atilde;, bạn sẽ dễ d&agrave;ng x&aacute;c định được ch&ograve;m sao Ti&ecirc;n Nữ. H&atilde;y d&ugrave;ng k&iacute;nh thi&ecirc;n văn cở trung b&igrave;nh, bạn sẽ quan s&aacute;t được thi&ecirc;n h&agrave; Andromeda, rất gần Hệ Mặt Trời đấy nh&eacute;!</p>\r\n\r\n<p><img alt="Bầu trời sao mùa thu" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/stellarium-008%20copy_zps9pfacqgu.png" /></p>\r\n\r\n<p style="text-align:center"><em>X&aacute;c định ch&ograve;m sao Ti&ecirc;n Nữ.</em></p>\r\n\r\n<p>Ch&ograve;m sao cuối c&ugrave;ng trong gia đ&igrave;nh ho&agrave;ng gia l&agrave; Anh Ti&ecirc;n. Sử dụng ch&ograve;m sao Thi&ecirc;n Hậu v&agrave; Ti&ecirc;n Nữ, bạn sẽ t&igrave;m thấy ng&ocirc;i sao Mirphak của anh ch&agrave;ng dũng sĩ n&agrave;y. Ch&ograve;m sao n&agrave;y l&agrave; t&acirc;m điểm của trận mưa sao băng Perseid xảy ra h&agrave;ng năm với cực điểm thường rơi v&agrave;o khoảng 12-13 th&aacute;ng 8. CLB Thi&ecirc;n văn học Đ&agrave; Nẵng &ndash; DAC thường xuy&ecirc;n tổ chức cắm trại để quan s&aacute;t hiện tượng thi&ecirc;n văn kỳ th&uacute; n&agrave;y.</p>\r\n\r\n<p><img alt="Bầu trời sao mùa thu" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/stellarium-011%20copy_zpsaqhu9eza.png" /></p>\r\n\r\n<p style="text-align:center"><em>Dựa v&agrave;o Ti&ecirc;n Nữ v&agrave; Thi&ecirc;n Hậu để x&aacute;c định ch&ograve;m sao Anh Ti&ecirc;n.</em></p>\r\n\r\n<p>B&acirc;y giờ, ch&uacute;ng ta c&ugrave;ng t&igrave;m hiểu v&igrave; sao ch&agrave;ng dũng sĩ Anh Ti&ecirc;n trở th&agrave;nh ph&ograve; m&atilde; trong gia đ&igrave;nh ho&agrave;ng gia nh&eacute;! Trong thần thoại Hy Lạp, Thi&ecirc;n Hậu &ndash; Cassiopeia tự cho rằng c&ocirc; con g&aacute;i cưng của b&agrave;, tức l&agrave; Andromeda &ndash; Ti&ecirc;n Nữ l&agrave; người đẹp nhất thế gian, thậm ch&iacute; c&ograve;n đẹp hơn cả thần ti&ecirc;n. Điều n&agrave;y l&agrave;m cho Poseidon &ndash; thần biển cả, v&agrave; cũng l&agrave; anh trai của thần Zeus rất tức giận, v&igrave; &ocirc;ng đ&atilde; tạo ra những sinh vật đẹp nhất trong h&igrave;nh dạng của nữ thần biển. Trong sự tức giận, &ocirc;ng tạo ra một con qu&aacute;i vật biển Cetus để t&agrave;n ph&aacute; vương quốc của Thi&ecirc;n Vương v&agrave; Thi&ecirc;n Hậu. Để tr&aacute;nh vương quốc bị t&agrave;n ph&aacute;, vị ho&agrave;ng hậu buộc phải hy sinh người con g&aacute;i duy nhất cho con qu&aacute;i vật biển gớm ghiếc n&agrave;y. N&agrave;ng c&ocirc;ng ch&uacute;a bị tr&oacute;i v&agrave;o một tảng đ&aacute; lớn nh&ocirc; ra biển v&agrave; bị bỏ lại đ&oacute; để chờ con qu&aacute;i vật biển khổng lồ Cetus.</p>\r\n\r\n<p>Khi Cetus tiến lại gần n&agrave;ng c&ocirc;ng ch&uacute;a th&igrave; dũng sĩ Anh Ti&ecirc;n &ndash; Perseus t&igrave;nh cờ đi ngang qua đ&oacute; v&agrave; t&igrave;m c&aacute;ch cứu n&agrave;ng. Ch&agrave;ng dũng sĩ d&ugrave;ng đầu của nữ thần t&oacute;c rắn Medusa m&agrave; ch&agrave;ng vừa mới giết được để ti&ecirc;u diệt qu&aacute;i vật biển. Qu&aacute;i vật Cetus tr&ocirc;ng thấy đầu của Medusa liền h&oacute;a th&agrave;nh đ&aacute;. Sau khi cứu được c&ocirc; c&ocirc;ng ch&uacute;a, ch&agrave;ng đ&atilde; cầu h&ocirc;n n&agrave;ng v&agrave; trở th&agrave;nh ph&ograve; m&atilde; trong gia đ&igrave;nh ho&agrave;ng gia.</p>\r\n\r\n<p><img alt="Bầu trời sao mùa thu" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/Perseus%20-%20Cetus_zpsouuqcae1.jpg" /></p>\r\n\r\n<p style="text-align:center"><em>Dũng sĩ Anh Ti&ecirc;n d&ugrave;ng đầu của Medusa để ti&ecirc;u diệt qu&aacute;i vật biển. Image: Internet</em></p>\r\n\r\n<h3>3 &ndash; C&Aacute;C CH&Ograve;M SAO HO&Agrave;NG ĐẠO</h3>\r\n\r\n<p>Chia tay với gia đ&igrave;nh ho&agrave;ng gia, ch&uacute;ng ta c&ugrave;ng nhau t&igrave;m hiểu về c&aacute;c ch&ograve;m sao ho&agrave;ng đạo nh&eacute;!</p>\r\n\r\n<p>Sau khi ho&agrave;ng h&ocirc;n kết th&uacute;c, ch&ograve;m sao Bọ Cạp với h&igrave;nh dạng chữ S đặc trưng, kh&aacute; giống bản đồ Việt Nam phần đất liền, đ&atilde; hiện r&otilde; ở gần ch&acirc;n trời ph&iacute;a t&acirc;y. T&acirc;m điểm của con bọ cạp n&agrave;y l&agrave; ng&ocirc;i sao Antares với m&agrave;u đỏ đặc trưng.</p>\r\n\r\n<p><img alt="Bầu trời sao mùa thu" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/stellarium-001%20copy_zpsz0fn13eg.png" /></p>\r\n\r\n<p style="text-align:center"><em>Ch&ograve;m sao Bọ Cạp.</em></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute; l&agrave; ch&ograve;m sao Cung Thủ quen thuộc với nh&oacute;m sao Ấm Tr&agrave; m&agrave; ch&uacute;ng ta đ&atilde; quan s&aacute;t ở m&ugrave;a h&egrave; vừa rồi. Ch&ograve;m sao n&agrave;y hiện ra như một nh&acirc;n m&atilde; đang giương cung bắn t&ecirc;n v&agrave;o con bọ cạp.</p>\r\n\r\n<p><img alt="Bầu trời sao mùa thu" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/stellarium-002%20copy_zpsz1fi8fb3.png" /></p>\r\n\r\n<p style="text-align:center"><em>Ch&ograve;m sao Bọ Cạp v&agrave; Cung Thủ.</em></p>\r\n\r\n<p>B&acirc;y giờ ch&uacute;ng ta sẽ t&igrave;m hiểu về ch&ograve;m sao ho&agrave;ng đạo Ma Kết. Ch&ograve;m sao n&agrave;y mang h&igrave;nh ảnh của con vật đầu d&ecirc; đu&ocirc;i c&aacute;. Tuy c&aacute;c ng&ocirc;i sao của ch&ograve;m sao n&agrave;y kh&ocirc;ng qu&aacute; s&aacute;ng nhưng ch&uacute;ng ta c&oacute; thể nhận biết n&oacute; kh&aacute; dễ v&igrave; vị tr&iacute; của n&oacute; nằm ngay b&ecirc;n cạnh ch&ograve;m sao Cung Thủ v&agrave; h&igrave;nh dạng tam gi&aacute;c vu&ocirc;ng c&acirc;n</p>\r\n\r\n<p><img alt="Bầu trời sao mùa thu" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/stellarium-016%20copy_zps4kyjx7dd.png" /></p>\r\n\r\n<p style="text-align:center"><em>Ch&ograve;m sao Cung Thủ v&agrave; Ma Kết.</em></p>\r\n\r\n<p>Trong thần thoại Hy Lạp, khi con qu&aacute;i vật Typoon đ&aacute;nh bại thần Zeus th&igrave; c&aacute;c vị thần kh&aacute;c đều sợ h&atilde;i v&agrave; chạy trốn. Họ đ&atilde; biến th&agrave;nh c&aacute;c con vật để con qu&aacute;i vật kh&ocirc;ng nhận ra. Thần Pan, một vị thần thiếu sự quyết đo&aacute;n, trong l&uacute;c nguy cấp ấy kh&ocirc;ng biết biến th&agrave;nh con vật g&igrave;, n&ecirc;n đ&atilde; xuống nước v&agrave; biến đầu m&igrave;nh th&agrave;nh con d&ecirc; ng&acirc;y thơ, c&ograve;n phần th&acirc;n dưới để kh&ocirc;ng bị ngập trong nước, thần đ&atilde; biến th&agrave;nh đu&ocirc;i c&aacute;.</p>\r\n\r\n<p><img alt="Bầu trời sao mùa thu" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/Capricornus_zpss9m4ogqu.jpg" /></p>\r\n\r\n<p style="text-align:center"><em>Ch&ograve;m sao Ma Kết. Image: Urania&rsquo;s Mirror.</em></p>\r\n\r\n<p>Sau đ&oacute;, thần Hermes giải tho&aacute;t được cho thần Zeus v&agrave; qu&aacute;i vật Typhoon bị đ&aacute;nh bại th&igrave; c&aacute;c thần mới trở lại h&igrave;nh dạng như cũ. H&igrave;nh ảnh &ldquo;đầu d&ecirc; đu&ocirc;i c&aacute;&rdquo; được đưa l&ecirc;n bầu trời th&agrave;nh ch&ograve;m sao Ma Kết để đưa ra b&agrave;i học cho mọi người khi quyết định những vấn đề quan trọng trong cuộc đời.</p>\r\n\r\n<p>Ch&ograve;m sao ho&agrave;ng đạo tiếp theo l&agrave; Bảo B&igrave;nh, mang h&igrave;nh ảnh của vị thần đ&atilde; đổ nước từ thi&ecirc;n đường xuống cho con người. Tuy nhi&ecirc;n, ch&ograve;m sao n&agrave;y gồm nhiều ng&ocirc;i sao mờ v&agrave; rất kh&oacute; để quan s&aacute;t ch&ograve;m sao n&agrave;y.</p>\r\n\r\n<p><img alt="Bầu trời sao mùa thu" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/stellarium-013%20copy_zpsayc85hag.png" /></p>\r\n\r\n<p style="text-align:center"><em>Ch&ograve;m sao Bảo B&igrave;nh v&agrave; Ma Kết.</em></p>\r\n\r\n<p>Ngay b&ecirc;n cạnh con ngựa Phi M&atilde; l&agrave; ch&ograve;m sao mang h&igrave;nh ảnh hai con c&aacute;: c&aacute; mẹ v&agrave; c&aacute; con, đ&oacute; l&agrave; Song Ngư. Ba ch&ograve;m sao ho&agrave;ng đạo Bảo B&igrave;nh, Ma Kết, Song Ngư, c&ugrave;ng rất nhiều ch&ograve;m sao kh&aacute;c nằm trong khu vực gọi l&agrave; &ldquo;Biển Trời&rdquo;, l&agrave; nơi c&oacute; rất nhiều ch&ograve;m sao li&ecirc;n quan đến biển cả như Qu&aacute;i Vật Biển, S&ocirc;ng C&aacute;i, Nam Ngư, C&aacute; Kiếm,&hellip;</p>\r\n\r\n<p><img alt="Bầu trời sao mùa thu" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/stellarium-018%20copy_zpstebu5srr.png" /></p>\r\n\r\n<p style="text-align:center"><em>Ch&ograve;m sao Song Ngư.</em></p>\r\n\r\n<p>Trong thần thoại Hy Lạp, khi qu&aacute;i vật Typhoon tấn c&ocirc;ng c&aacute;c vị thần, trong l&uacute;c thần Pan biến th&agrave;nh con vật đầu d&ecirc; đu&ocirc;i c&aacute; th&igrave; nữ thần sắc đẹp Aphrodite v&agrave; con trai l&agrave; Eros &ndash; thần t&igrave;nh y&ecirc;u đ&atilde; biến th&agrave;nh hai con c&aacute;: c&aacute; mẹ v&agrave; c&aacute; con. Để hai mẹ con khỏi lạc nhau, nữ thần Aphrodite đ&atilde; quấn hai mẹ con lại với nhau.</p>\r\n\r\n<p><img alt="Bầu trời sao mùa thu" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/Pisces_zpsp17wnvh3.jpg" /></p>\r\n\r\n<p style="text-align:center"><em>Ch&ograve;m sao Song Ngư. Image: Urania&rsquo;s Mirror.</em></p>\r\n\r\n<p>Tiếp đến, ch&uacute;ng ta c&ugrave;ng nhau đến với ch&ograve;m sao Bạch Dương nh&eacute;! Ch&ograve;m sao n&agrave;y nằm giữa Song Ngư v&agrave; Anh Ti&ecirc;n. Nếu c&oacute; bản đồ sao trong tay, bạn sẽ dễ d&agrave;ng nhận biết được n&oacute; v&igrave; c&aacute;c ng&ocirc;i sao của n&oacute; cũng kh&aacute; s&aacute;ng đấy!</p>\r\n\r\n<p><img alt="Bầu trời sao mùa thu" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/stellarium-022%20copy_zpsqho29smu.png" /></p>\r\n\r\n<p style="text-align:center"><em>Ch&ograve;m sao Bạch Dương nằm giữa Anh Ti&ecirc;n v&agrave; Song Ngư.</em></p>\r\n\r\n<p>Trong thần thoại Hy Lạp, vua Athamus xứ Boetia v&agrave; ho&agrave;ng hậu Nephele sinh hạ hai người con l&agrave; ho&agrave;ng tử Phrixus v&agrave; c&ocirc;ng ch&uacute;a Helle. Tuy nhi&ecirc;n, nh&agrave; vua lại say đắm một người con g&aacute;i kh&aacute;c l&agrave; Ino n&ecirc;n &ocirc;ng đ&atilde; quyết định ly h&ocirc;n với vợ m&igrave;nh v&agrave; sắc phong Ino l&agrave;m ho&agrave;ng hậu mới.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, người mẹ kế Ino lại t&igrave;m c&aacute;ch s&aacute;t hại hai đứa con của chồng m&igrave;nh: ho&agrave;ng tử Phrixus v&agrave; c&ocirc;ng ch&uacute;a Helle. Ở vương quốc n&agrave;y, bắp l&agrave; lương thực ch&iacute;nh, v&igrave; vậy n&ecirc;n người mẹ kế ra lệnh cho những người phụ nữ vương quốc n&agrave;y rang bắp l&ecirc;n trước khi gieo trồng. Đồng thời, Ino c&ograve;n hối lộ nh&agrave; ti&ecirc;n tri &ndash; người m&agrave; vua sai đi hỏi c&aacute;c vị thần về hiện tượng m&ugrave;a m&agrave;ng thất b&aacute;t &ndash; để &ocirc;ng ta n&oacute;i dối rằng 2 đứa con ch&iacute;nh l&agrave; nguồn gốc của hiểm họa v&agrave; phải tế họ cho thần th&igrave; m&ugrave;a m&agrave;ng với trở lại.</p>\r\n\r\n<p>Vua v&agrave; tất cả d&acirc;n ch&uacute;ng đều tin v&agrave;o lời ti&ecirc;n tri độc &aacute;c nọ, họ quyết định hy sinh hai đứa trẻ. Lo cho sự an to&agrave;n của con, Nephele &ndash; mẹ ruột hai đứa trẻ đ&atilde; ph&aacute;i một người bảo vệ đội lốt con cừu c&oacute; l&ocirc;ng bằng v&agrave;ng do thần Zeus tặng cho b&agrave;.</p>\r\n\r\n<p><img src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/Aries_zpst5mr3n4r.jpeg" /></p>\r\n\r\n<p style="text-align:center"><em>Ho&agrave;ng tử Phrixus v&agrave; c&ocirc;ng ch&uacute;a Helle tr&ecirc;n lưng con cừu c&oacute; bộ l&ocirc;ng v&agrave;ng &ndash; Image: Bảo t&agrave;ng Nghệ thuật Bridgeman.</em></p>\r\n\r\n<p>Ng&agrave;y tế lễ đến, con cừu chở ho&agrave;ng tử v&agrave; c&ocirc;ng ch&uacute;a bỏ chạy băng qua đại dương nhưng chẳng may Helle bị rơi xuống biển. Con cừu vừa quay đầu nh&igrave;n c&ocirc; em g&aacute;i, lại vừa bảo vệ người anh trai tiếp tục cuộc chạy trốn.</p>\r\n\r\n<p>Bộ l&ocirc;ng v&agrave;ng của ch&uacute; cừu đực n&agrave;y về sau trở th&agrave;nh b&aacute;u vật khuynh th&agrave;nh của vương quốc Colchis, một vương quốc kh&aacute;c, nơi hai anh em được che giấu suốt đời tho&aacute;t khỏi người mẹ kế Ino. Ch&iacute;nh v&igrave; bộ l&ocirc;ng ấy m&agrave; Jason, người anh h&ugrave;ng trong thần thoại Hy Lạp đ&atilde; t&igrave;m kiếm trong suốt h&agrave;nh tr&igrave;nh truy t&igrave;m bộ l&ocirc;ng cừu v&agrave;ng nổi tiếng của m&igrave;nh, được nhắc đến trong c&acirc;u chuyện về cuộc viễn chinh của nh&oacute;m thủy thủ t&agrave;u Argo.</p>\r\n\r\n<p><img alt="Bầu trời sao mùa thu" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/jason3_zpskkhblyph.jpg" /></p>\r\n\r\n<p style="text-align:center"><em>Jason v&agrave; con t&agrave;u Argo &ndash; Phim Jason and the Argonauts của đạo diễn Don Chaffey.</em></p>\r\n\r\n<p>V&igrave; ơn huệ đ&oacute;, Zeus đ&atilde; đưa con cừu c&oacute; bộ l&ocirc;ng m&agrave;u v&agrave;ng l&ecirc;n th&agrave;nh một ch&ograve;m sao, do đ&oacute; mới c&oacute; ch&ograve;m sao Bạch Dương như ng&agrave;y nay.</p>\r\n\r\n<h3>4 &ndash; C&Aacute;C CH&Ograve;M SAO KH&Aacute;C</h3>\r\n\r\n<p>Th&ocirc;ng thường, ch&uacute;ng ta thường quan s&aacute;t nhiều c&aacute;c ch&ograve;m sao ở ph&iacute;a bắc, m&agrave; &iacute;t quan t&acirc;m đến c&aacute;c ch&ograve;m sao ph&iacute;a nam hơn.</p>\r\n\r\n<p>Nếu ở hướng nam, bạn quan s&aacute;t thấy 4 ng&ocirc;i sao s&aacute;ng, tạo th&agrave;nh h&igrave;nh tứ gi&aacute;c th&igrave; rất c&oacute; thể đ&oacute; l&agrave; ng&ocirc;i sao Fomalhaut của ch&ograve;m sao Nam Ngư v&agrave; ba ng&ocirc;i sao s&aacute;ng của ch&ograve;m sao Chim Sếu đấy.</p>\r\n\r\n<p><img alt="Bầu trời sao mùa thu" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/stellarium-020%20copy_zpsxk8uyh6v.png" /></p>\r\n\r\n<p style="text-align:center"><em>Ch&ograve;m sao Nam Ngư v&agrave; Chim Sếu.</em></p>\r\n\r\n<p>Để quan s&aacute;t c&aacute;c ch&ograve;m sao kh&aacute;c, c&aacute;c bạn h&atilde;y tải bản đồ sao dưới đ&acirc;y v&agrave; in n&oacute; ra giấy k&iacute;ch thước lớn nh&eacute;, n&oacute; sẽ gi&uacute;p &iacute;ch cho bạn rất nhiều trong việc nhận biết c&aacute;c ch&ograve;m sao đấy!</p>\r\n\r\n<p><img alt="Bầu trời sao mùa thu" src="https://i65.photobucket.com/albums/h220/ThaiVanLoiDN/Thang%209%20A%20copy_zpsosp5aath.png" /></p>\r\n\r\n<p style="text-align:center"><em>Bản đồ sao m&ugrave;a thu. Image: Th&aacute;i Lợi &ndash; DAC</em></p>\r\n\r\n<p>Bầu trời sao tuyệt đẹp, c&aacute;c bạn h&atilde;y lựa chọn một địa điểm quan s&aacute;t thật tốt, &iacute;t bị ảnh hưởng bởi &aacute;nh s&aacute;ng v&agrave; chi&ecirc;m ngưỡng vẻ đẹp của n&oacute; trong những ng&agrave;y trời quang, &iacute;t m&acirc;y nh&eacute;!</p>\r\n\r\n<p>Trong b&agrave;i c&oacute; sử dụng c&aacute;c bức ảnh từ phần mềm Stellarium.</p>\r\n\r\n<p style="text-align:right"><em>Th&aacute;i Lợi &ndash; DAC</em></p>', '1492914549.png', 4, 3, 6, '2017-04-22 19:29:09', '2017-05-10 08:49:12', NULL),
(24, 'Có phải các ngôi sao luôn di chuyển cùng nhau trên bầu trời?', 'Nhiều bạn thắc mắc với mình rằng: Có phải các ngôi sao chòm sao luôn đi liền với nhau hay không, cùng nhau di chuyển hay sao mà qua một thời gian dài, hình dạng nó vẫn không thay đổi?', '<p>Nhiều bạn thắc mắc với m&igrave;nh rằng: C&oacute; phải c&aacute;c ng&ocirc;i sao ch&ograve;m sao lu&ocirc;n đi liền với nhau hay kh&ocirc;ng, c&ugrave;ng nhau di chuyển hay sao m&agrave; qua một thời gian d&agrave;i, h&igrave;nh dạng n&oacute; vẫn kh&ocirc;ng thay đổi?</p>\r\n\r\n<p><img alt="BacDau%203_zpsz94ycsuh - Có phải các ngôi sao luôn di chuyển cùng nhau trên bầu trời?" src="http://i0.wp.com/i1168.photobucket.com/albums/r491/vuong_anh2/BacDau%203_zpsz94ycsuh.png?resize=980%2C724" /></p>\r\n\r\n<p>C&acirc;u trả lời, như c&aacute;c nh&igrave;n v&agrave;o trong h&igrave;nh ảnh n&agrave;y cũng đ&atilde; r&otilde;. C&aacute;c ng&ocirc;i sao, c&aacute;c thi&ecirc;n thể kh&aacute;c trong vũ trụ (kh&ocirc;ng t&iacute;nh những thi&ecirc;n thể thuộc Hệ Mặt Trời), đều rất xa ch&uacute;ng ta. Mỗi ng&ocirc;i sao hay thi&ecirc;n thể di chuyển theo những c&aacute;ch kh&ocirc;ng giống nhau. Nhưng điều quan trọng ở đ&acirc;y, l&agrave; ch&uacute;ng rất xa, rất rất rất xa. V&igrave; vậy, n&ecirc;n qua h&agrave;ng trăm năm, ta vẫn thấy n&oacute; lu&ocirc;n &ldquo;đi liền&rdquo; với nhau. Tuy nhi&ecirc;n, nếu qua một khoảng thời gian d&agrave;i hơn, th&igrave; n&oacute; sẽ kh&aacute;c nhau nhiều, như h&igrave;nh ảnh dưới đ&acirc;y.</p>\r\n\r\n<p><img alt="BacDau%20copy_zpsynde160k - Có phải các ngôi sao luôn di chuyển cùng nhau trên bầu trời?" src="http://i1.wp.com/i1168.photobucket.com/albums/r491/vuong_anh2/BacDau%20copy_zpsynde160k.png?resize=980%2C724" /></p>\r\n\r\n<p>Tr&ecirc;n bầu trời, những ng&ocirc;i sao v&agrave; c&aacute;c thi&ecirc;n thể đều chuyển động từ đ&ocirc;ng sang t&acirc;y, theo những đường tr&ograve;n. T&acirc;m của những đường tr&ograve;n n&agrave;y l&agrave; g&igrave;? V&acirc;ng, đ&oacute; ch&iacute;nh l&agrave; ng&ocirc;i sao lu&ocirc;n đứng y&ecirc;n một chỗ (nếu chỉ x&eacute;t trong khoảng thời gian v&agrave;i trăm năm gần đ&acirc;y, v&agrave; bỏ qua sai số nhỏ), ng&ocirc;i sao chỉ hướng ch&iacute;nh x&aacute;c gần như tuyệt đối hướng Bắc, v&agrave; đ&oacute; l&agrave; ng&ocirc;i sao Bắc Cực.</p>\r\n\r\n<p><img alt="stars-night-1149134_960_720_zpsbytzscpm - Có phải các ngôi sao luôn di chuyển cùng nhau trên bầu trời?" src="http://i1168.photobucket.com/albums/r491/vuong_anh2/stars-night-1149134_960_720_zpsbytzscpm.jpg" /></p>\r\n\r\n<p style="text-align:center"><em>C&aacute;c ng&ocirc;i sao di chuyển theo c&aacute;c đường tr&ograve;n, t&acirc;m l&agrave; sao Bắc Cực.</em></p>\r\n\r\n<p style="text-align:right"><em><strong>Th&aacute;i Lợi &ndash; DAC</strong></em></p>', '1492914610.jpg', 2, 3, 6, '2017-04-22 19:30:10', '2017-04-26 05:47:35', NULL);

