INSERT INTO `quotes` (`id`, `author`, `image`, `content`, `created_at`, `updated_at`, `deleted_at`) VALUES
(1, 'Bill Gates', '1492914795.jpg', 'Cuộc sống vốn không công bằng.\r\nHãy tập quen dần với điều đó!', '2017-04-17 01:24:31', '2017-04-22 19:33:15', NULL),
(2, 'Steve Jobs', '1492914831.jpg', 'Chúng ta không có cơ hội để làm nhiều thứ trong đời, cuộc sống này quá ngắn ngủi. Vì vậy bạn cần sống sao cho thật xứng đáng', '2017-04-17 01:26:13', '2017-04-22 19:33:51', NULL),
(3, 'Nguyễn Hà Đông', '1492914861.jpg', 'Người ta thường thích thú những thứ không hoàn thiện', '2017-04-17 01:38:42', '2017-04-22 19:34:21', NULL),
(4, 'John Forbes Nash', '1492915141.jpg', 'Người ta luôn luôn có ý nghĩ rằng những người bệnh tâm thần đang sống khổ sở. Tôi nghĩ sự điên rồ cũng có thể là một kiểu giải thoát. Nếu mọi chuyện quá tồi tệ, người ta có thể muốn tưởng tượng điều gì đó tốt đẹp hơn.', '2017-04-17 01:39:59', '2017-04-22 19:39:01', NULL),
(5, 'Albert Einstein', '1492915730.jpg', 'Trí tưởng tượng quan trọng hơn tri thức. Vì tri thức chỉ giới hạn trong những gì ta hiểu biết, trong khi trí tưởng tượng bao trùm cả thế giới, và cả những gì ta sẽ hiểu biết.', '2017-04-17 01:41:44', '2017-04-22 19:48:50', NULL),
(6, 'Voluptatibus amet eos nostrum sequi quo mollitia adipisicing nulla totam quam debitis sit assumenda in molestiae', '', 'Voluptatibus amet eos nostrum sequi quo mollitia adipisicing nulla totam quam debitis sit assumenda in molestiae. Voluptatibus amet eos nostrum sequi quo mollitia adipisicing nulla totam quam debitis sit assumenda in molestiae', '2017-04-17 01:41:56', '2017-04-22 19:50:52', '2017-04-22 19:50:52'),
(7, 'Veritatis voluptatibus quas suscipit debitis quis molestiae incidunt dolorem quaerat', '1492424519.jpg', 'Ipsum asperiores enim at non omnis dolor aliquip dolores accusantium et ratione reprehenderit doloribus est ad reprehenderit, voluptatum labore consequatur.', '2017-04-17 01:42:12', '2017-04-22 19:50:55', '2017-04-22 19:50:55'),
(8, 'Hic laudantium assumenda dolor et rerum quas sint quod', '', 'Odio quae consequatur? Animi, praesentium cum ut obcaecati inventore incidunt, earum esse perferendis dolores voluptas deserunt velit adipisci quaerat at.', '2017-04-17 01:46:54', '2017-04-17 02:09:40', '2017-04-17 02:09:40'),
(9, 'Laborum et sunt unde assumenda tempore consequatur unde cillum cumque ad', '1492421558.jpeg', 'Maxime aut rem ut fugiat molestiae iusto odit blanditiis dolor id, eius vitae ea quis esse, sint, molestiae.', '2017-04-17 02:32:01', '2017-04-17 02:32:42', '2017-04-17 02:32:42');

