INSERT INTO `slider` (`id`, `name`, `image`, `link`, `description`, `created_at`, `updated_at`, `deleted_at`) VALUES
(1, 'Madison Buck', '1492446569.jpg', 'Laborum Non error pariatur Excepteur architecto velit incididunt dolor deserunt et', 'Ipsam est, veniam, consequat. Minim elit, consequatur, nostrud magnam ipsam totam qui ea tempora autem accusantium laborum blanditiis dicta.', '2017-04-17 09:28:40', '2017-04-17 09:29:37', '2017-04-17 09:29:37'),
(2, 'Giới trẻ Đà Nẵng đổ xô ra biển ngắm nhật thực', '1493212013.png', '/gioi-tre-da-nang-do-xo-ra-bien-don-nhat-thuc-13.html', 'Từ sáng sớm hôm nay, các thành viên CLB Thiên văn học Đà Nẵng cùng những người yêu mến thiên văn đã tới bãi biển xinh đẹp tại công viên Biển Đông để chiêm ngưỡng hiện tượng nhật thực một phần.', '2017-04-17 09:32:26', '2017-04-26 06:06:53', NULL),
(3, 'Hướng dẫn quan sát sao Bắc Cực', '1493212027.png', '/huong-dan-quan-sat-sao-bac-cuc-19.html', 'Polaris (Alpha Ursar Minoris) là ngôi sao sáng nhất trong chòm sao Tiểu Hùng (Ursa Minor). Hiện nay, Alpha Ursar Minoris là ngôi sao gần thiên cực Bắc nhất, nên nó được chọn là sao Bắc Cực.', '2017-04-17 09:32:33', '2017-04-26 06:07:07', NULL),
(4, 'Bầu trời sao mùa thu', '1493212036.png', '/bau-troi-sao-mua-thu-23.html', 'Bầu trời sao mùa thu nổi bật với chòm sao Phi Mã, các chòm sao hoàng gia và các chòm sao hoàng đạo như Ma Kết, Bảo Bình, Song Ngư,…', '2017-04-17 20:40:45', '2017-04-26 06:07:16', NULL);

